void  modified()
{
    super();

    if (suppItemTable.AccountCode == TableGroupAll::Table)
    {
        if (module == ModuleInventCustVend::Vend)
        {
            vendTable = VendTable::find(suppItemTable.AccountRelation);
            element.initFromVendTable();
        }
        else
        {
            custTable = CustTable::find(suppItemTable.AccountRelation);
            element.initFromCustTable();
        }
    }
}

