    element.updateDesign(InventDimFormDesignUpdate::FieldChange, true);
}

void  modified()
{
    super();

    if (suppItemTable.ItemCode == TableGroupAll::Table)
    {
        inventTable = InventTable::find(suppItemTable.ItemRelation);
        element.initFromInventTable();
    }
    element.updateDesign(InventDimFormDesignUpdate::FieldChange, true);
}

