boolean modified()
{
    boolean ret;

    if (suppItemTable.Module != ModuleInventCustVend::Invent)
        suppItemTable.write();

    ret = super();

    if (fromModule == ModuleInventCustVend::Invent)
        itemCode    = relationCode.selection();
    else
        accountCode = relationCode.selection();

    element.reSelect();

    return ret;
}

boolean validate()
{
    boolean ret;

    ret = super();

    if (ret && suppItemTable.Module != ModuleInventCustVend::Invent)
        ret = suppItemTable_ds.validateWrite();

    return ret;
}

