/// <summary>
/// Enables and disables form controls based on the status
/// of the selected promotion.
/// </summary>
void enableByStatus()
{
    boolean enable;

    enable = TAMFundTable.Status == TAMFundStatus::Planning;

    GeneralGrp_Type.allowEdit(enable);
    GeneralGrp_FromDate.allowEdit(enable);
    GeneralGrp_ToDate.allowEdit(enable);
    GeneralGrp_TotalFundAmt.allowEdit(enable);
    GeneralGrp_Details.allowEdit(enable);
    GeneralGrp_Usage.allowEdit(enable);

    FundAmounts.allowEdit(enable);

    CustAddRemoveButtons.enabled(enable);
    TAMRetailAddItemsButton.enabled(enable);
    TAMFundInvent_ds.allowCreate(enable);
    RemoveButton.enabled(enable);

    InventDim_ds.allowEdit(enable);
}

/// <summary>
/// Initializes the dimension display for the form.
/// </summary>
public void init()
{
    super();

    tamPromotionParameters = TAMPromotionParameters::find();

    // Invent dim fields.
    element.updateDesign(InventDimFormDesignUpdate::Init);
}

/// <summary>
/// Initializes the object used to control the customer category tree object.
/// </summary>
void initializeHelper_Category()
{
    QueryBuildRange     range;
    MCRCustCategory     mcrCustCategoryLocal;

    currentCategoryHierarchy = MCRCustCategoryHierarchy::findByName(MCRCustCategoryHierarchy_Name.text());

    range = MCRCustCategoryHierarchy_ds.query().dataSourceTable(tableNum(MCRCustCategoryHierarchy)).addRange(fieldNum(MCRCustCategoryHierarchy, RecId));
    range.value( queryValue(currentCategoryHierarchy.RecId ) );
    MCRCustCategoryHierarchy_ds.executeQuery();

    range = MCRCustCategory_ds.query().dataSourceName(MCRCustCategory_ds.name()).addRange(fieldNum(MCRCustCategory, CategoryHierarchy));
    range.value( queryValue(currentCategoryHierarchy.RecId ) );
    MCRCustCategory_ds.executeQuery();

    mcrCustCategoryLocal = MCRCustCategory::getRoot(currentCategoryHierarchy.RecId);

    if (mcrCustCategoryLocal)
    {
        hierarchyTree = new MCRCustCategoryTreeDatasource(MCRCustCategory_ds, ctrlCustTree,
                                                            fieldNum(MCRCustCategory, RecId),
                                                            fieldNum(MCRCustCategory, ParentCategory),
                                                            fieldNum(MCRCustCategory, Name),
                                                            false,
                                                            true,
                                                            fieldNum(MCRCustCategory, Code),
                                                            currentCategoryHierarchy,
                                                            fieldNum(MCRCustCategory, NestedSetLeft) );

        hierarchyTree.initRoot(mcrCustCategoryLocal.Name, mcrCustCategoryLocal.RecId, hierarchyTree.image());
        ctrlCustTree.select(0);
    }
}

/// <summary>
/// Initializes the object used to control the funds customer category tree object.
/// </summary>
void initializeHelper_Fund()
{
    TAMFundCustCategory     tamFundCustCategoryRoot;

    // Fund logic.
    tamFundCustCategoryRoot = TAMFundCustCategory::getRoot(TAMFundTable.FundID);
    if (tamFundCustCategoryRoot)
    {
        FundTree = new TAMFundCategoryTreeDatasource(TAMFundCustCategory_ds, ctrlFundTree,
                                                        fieldNum(TAMFundCustCategory, RecId),
                                                        fieldNum(TAMFundCustCategory, ParentCategory),
                                                        fieldNum(TAMFundCustCategory, Name),
                                                        false,
                                                        true,
                                                        fieldNum(TAMFundCustCategory, Code),
                                                        TAMFundTable,
                                                        fieldNum(TAMFundCustCategory, NestedSetLeft) );

        FundTree.initRoot(tamFundCustCategoryRoot.Name, tamFundCustCategoryRoot.RecId, FundTree.image());
    }
}

/// <summary>
/// Gets the values that should show for inventory dimension fields.
/// </summary>
/// <returns>
/// The <c>inventDimFormSetup</c> object that defines the values
/// that should show for inventory dimension fields.
/// </returns>
Object inventDimSetupObject()
{
    return inventDimFormSetup;
}

/// <summary>
/// Sets the selected hierarchy records and initializes the trees.
/// </summary>
public void run()
{
    super();

    MCRCustCategoryHierarchy_Name.text(TAMFundTable.custCategoryHierarchy);

    element.initializeHelper_Category();
    element.initializeHelper_Fund();
}

/// <summary>
/// Hides and shows the inventory dimension fields on an item.
/// </summary>
/// <param name="mode">
/// Action used to determine which fields should be hidden and shown.
/// </param>
void updateDesign(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetup)
            {
                inventDimFormSetup = InventDimCtrl_Frm_TAM::newFromForm(element);
                inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemIdProductDimensionsOnly(TAMFundInvent.ItemID));
                inventDimFormSetup.formSetControls(true);
            }
            break;
        case InventDimFormDesignUpdate::Active:
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemIdProductDimensionsOnly(TAMFundInvent.ItemID));
            inventDimFormSetup.formSetControls(true);
            break;
        case InventDimFormDesignUpdate::FieldChange:
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemIdProductDimensionsOnly(TAMFundInvent.ItemID));
            inventDimFormSetup.formSetControls(false);
            break;
        default: throw error(strfmt("@SYS54195", funcname()));
    }
}

public void write()
{
    recordUpdated = true;

    super();
}

public void create(boolean _append = false)
{
    numberSeq numberSeq;
    super(_append);

    // Set the fundID of the new fund.
    numberSeq = NumberSeq::newGetNum(TAMPromotionParameters::numRefFundID());
    TAMFundTable.FundID = numberSeq.num();
}

public int active()
{
    int ret;

    if (!recordUpdated)
    {
        ctrlFundTree.deleteAll();
        ctrlCustTree.deleteAll();

        // Builds the fund hierarchy on existing funds when the fund is selected.
        MCRCustCategoryHierarchy_Name.enabled(TAMFundTable.custCategoryHierarchy == '');

        if (TAMFundTable.custCategoryHierarchy == '')
        {
            MCRCustCategoryHierarchy_Name.text(tamPromotionParameters.DefaultCustHierarchy);
        }
        else
        {
            MCRCustCategoryHierarchy_Name.text(TAMFundTable.custCategoryHierarchy);
        }

        element.initializeHelper_Fund();
        element.initializeHelper_Category();
    }

    ret = super();

    element.enableByStatus();

    //Reset recordUpdated
    recordUpdated = false;

    return ret;
}

/// <summary>
/// Enables and disables fields based on the status.
/// </summary>
public void modified()
{
    super();
    element.enableByStatus();
}

public void modified()
{
    super();

    // When the funds budgeted amount is modified, need to refresh the datasource
    if (TAMFundTable)
    {
        TAMFundCustCategory_ds.executeQuery();

        if (TAMFundTable.validateWrite())
        {
            TAMFundTable.update();
        }
    }
}

public void write()
{
    //Sets the inventDim to the selected values before saving.
    TAMFundInvent.InventDimID = InventDim::findOrCreate(InventDim).inventDimId;
    super();
    if (TAMFundInvent.InventDimID != InventDim.inventDimId)
    {
        InventDim.data(InventDim::find(TAMFundInvent.InventDimID));
        InventDim_DS.setCurrent();
    }
}

public boolean validateWrite()
{
    boolean ret;

    TAMFundInvent.InventDimID = InventDim::findOrCreate(InventDim).inventDimId;

    ret = super();

    return ret;
}

public int active()
{
    int ret;

    ret = super();

    element.updateDesign(InventDimFormDesignUpdate::Active);

    return ret;
}

public void modified()
{
    super();
    if (TAMFundInvent.ItemId != InventTable.ItemId)
    {
        InventTable.data(InventTable::find(TAMFundInvent.ItemId));
        InventTable_ds.setCurrent();
        element.updateDesign(InventDimFormDesignUpdate::FieldChange);
    }
}

public boolean validateWrite()
{
    // The InventTable datasource is readonly so we do not want to write anything.
    // The validateWrite in InventTable executes UOM conversion product variant lookup
    return true;
}

public void write()
{
}

public void modified()
{
    super();

    // When a fund categories budgeted amount is modified, need to refresh the datasource.
    TAMFundCustCategory_ds.executeQuery();

    TAMFundTable_ds.reread();
    TAMFundTable_ds.refresh();
}

