public void expanded(int _idx, FormTreeExpand _action, anytype _data)
{
    super(_idx, _action, _data);

    if ((FormTreeExpand::Expand == _action) && hierarchyTree)
    {
        hierarchyTree.updateTree(_idx);
    }
}

boolean expanding(int _idx, FormTreeExpand _action, anytype _data)
{
    boolean ret;

    ret = super(_idx, _action, _data);

    if (hierarchyTree)
    {
        hierarchyTree.expanding(_idx, _action, _data);
    }

    return ret;
}

/// <summary>
/// Adds the selected customer in the customer category
/// to the fund hierarchy.
/// </summary>
void clicked()
{
    if (hierarchyTree && ctrlCustTree.getRoot() != 0)
    {
        if (!TAMFundCustCategory::getRoot(TAMFundTable.FundID))
        {
            ttsBegin;
            TAMFundTable.custCategoryHierarchy = currentCategoryHierarchy.Name;
            TAMFundTable.update();
            ttsCommit;
            TAMFundTable_ds.reread();
            MCRCustCategoryHierarchy_Name.enabled(false);
            TAMFundCustCategory::createHierarchyRoot(TAMFundTable.FundID, TAMFundTable.FundID);
            element.initializeHelper_Fund();
        }
        FundTree.copyCategory(ctrlCustTree);
        element.initializeHelper_Fund();
    }
    else
    {
        error("@MCR39131");
    }
}

/// <summary>
/// Removes the selected customer from the current fund.
/// </summary>
void clicked()
{
    super();

    if (FundTree)
    {
        FundTree.delete();
        if (!TAMFundCustCategory::getRoot(TAMFundTable.FundID))
        {
            ttsBegin;
            TAMFundTable.custCategoryHierarchy = '';
            TAMFundTable.update();
            ttsCommit;
            TAMFundTable_ds.reread();
            MCRCustCategoryHierarchy_Name.enabled(true);
        }
    }
}

public void expanded(int _idx, FormTreeExpand _action, anytype _data)
