        FundTree.expanding(_idx, _action, _data);
    }

    return ret;
}

void selectionChanged(FormTreeItem oldItem, FormTreeItem newItem, FormTreeSelect how)
{
    int root = this.getRoot();

    super(oldItem, newItem, how);

    if (FundTree)
    {
        FundTree.selectionChanged(oldItem, newItem);
    }

    TAMFundCustCategory_BudgetedAmount.allowEdit(newItem.idx() != root);
}

public boolean selectionChanging(FormTreeItem _oldItem, FormTreeItem _newItem, FormTreeSelect _how)
{
    boolean ret;

    ret = super(_oldItem, _newItem, _how);

    if (!ret && FundTree)
    {
        ret = FundTree.selectionChanging(_oldItem, _newItem, _how);
    }

    return ret;
}

void clicked()
{
    FormRun formRun;
    Args    args = new Args();

    args.record(TAMFundTable);
    args.caller(element);

    formRun = new MenuFunction(menuitemDisplayStr(TAMRetailAddItems), MenuItemType::Display).create(args);
    if (formRun)
    {
        formRun.run();
        formRun.wait();
        TAMFundInvent_ds.executeQuery();
    }
}
