/// <summary>
/// Enables and disables trade allowance promotion status buttons.
/// </summary>
void enableStatusButtons()
{
    boolean isDeleteEnabled = false;
    switch (TAMTradePromotion.PromotionStatus)
    {
        case TAMStatus::InProcess:
            InProcessButton.enabled(false);

            boolean workflowEnabled = WorkFlowTable::findActiveTemplate(workFlowTypeStr(TAMPromoMgmtApproval)).RecId != 0;
            // Check if there is an active workflow for the promotions management approval workflow.
            // If there is, enable the button accordingly.
            if (workflowEnabled)
            {
                InternalApprovedButton.enabled(TAMTradePromotion.WFApprovalStatus == TAMPromoMgmtApprovalStatus::Approved);
                ConfirmedButton.enabled(TAMTradePromotion.WFApprovalStatus == TAMPromoMgmtApprovalStatus::Approved);
                ClosedButton.enabled(false);
                isDeleteEnabled = TAMTradePromotion.wfApprovalStatus != TAMPromoMgmtApprovalStatus::Submitted;
            }
            else
            {
                InternalApprovedButton.enabled(true);
                ConfirmedButton.enabled(true);
                ClosedButton.enabled(true);
                isDeleteEnabled = true;
            }
            break;
        case TAMStatus::ManagementApproved:
            InProcessButton.enabled(true);
            InternalApprovedButton.enabled(false);
            ConfirmedButton.enabled(true);
            ClosedButton.enabled(true);
            break;
        case TAMStatus::CustomerApproved:
            InProcessButton.enabled(!TAMTradePromotion.checkMerchEventTrans(TAMTradePromotion.PromotionID));
            InternalApprovedButton.enabled(false);
            ConfirmedButton.enabled(false);
            ClosedButton.enabled(true);
            break;
        case TAMStatus::Closed:
            InProcessButton.enabled(!TAMTradePromotion.checkMerchEventTrans(TAMTradePromotion.PromotionID));
            InternalApprovedButton.enabled(false);
            ConfirmedButton.enabled(false);
            ClosedButton.enabled(false);
            break;
    }
    this.updateDeleteStatus(isDeleteEnabled);
}

public void updateMerchandiseCost()
{
    TAMCalcMerchandiseCost TAMCalcMerchandiseCost;

    // Calculate merchandise cost.
    TAMCalcMerchandiseCost = new TAMCalcMerchandiseCost(TAMPromoMerchEvent::Promotion,
                                                            '',
                                                            TAMTradePromotion.PromotionID,
                                                            TAMTradePromotion.CustAccount);
    TAMCalcMerchandiseCost.calcPromotionCost();
    TAMTradePromotion_ds.reread();
    TAMTradePromotion_ds.refreshEx();
}

public int active()
{
    int ret;

    ret = super();

    element.enableStatusButtons();

    return ret;
}

void clicked()
{
    super();
    ttsbegin;
    TAMTradePromotion.PromotionStatus = TAMStatus::InProcess;
    TAMTradePromotion.WFApprovalStatus = TAMPromoMgmtApprovalStatus::NotSubmitted;
    TAMTradePromotion.update();
    ttscommit;

    TAMTradePromotion_ds.reread();
    TAMTradePromotion_ds.refresh();

    element.enableStatusButtons();
    TAMTradePromotion.setRebateAgreementStatus();
    TAMTradePromotion_ds.refresh();
}

void clicked()
{
    super();
    ttsbegin;
    TAMTradePromotion.PromotionStatus = TAMStatus::ManagementApproved;
    TAMTradePromotion.update();
    ttscommit;
    element.enableStatusButtons();
    TAMTradePromotion.setRebateAgreementStatus();
    TAMTradePromotion_ds.refresh();
}

void clicked()
{
    super();
    ttsbegin;
    TAMTradePromotion.PromotionStatus = TAMStatus::CustomerApproved;
    TAMTradePromotion.update();
    ttscommit;
    element.enableStatusButtons();
    TAMTradePromotion.setRebateAgreementStatus();
    TAMTradePromotion_ds.refresh();
}

void clicked()
{
    super();
    ttsbegin;
    TAMTradePromotion.PromotionStatus = TAMStatus::Closed;
    TAMTradePromotion.update();
    ttscommit;
    element.enableStatusButtons();
    TAMTradePromotion.setRebateAgreementStatus();
    TAMTradePromotion_ds.refresh();
}

/// <summary>
/// Copies a trade promotion or trade promotion template.
/// </summary>
void clicked()
{
    Args            args;
    FormRun         formRun;

    element.updateMerchandiseCost();

    args = new Args();
    args.record(TAMTradePromotion);
    formRun = new MenuFunction(menuitemdisplaystr(TAMCopyTradePromotions), MenuItemType::Display).create(args);
    formRun.run();
    formRun.wait();

    if (formRun.closedOk())
    {
        info("@MCR26160");
    }

    super();

    TAMTradePromotion_ds.research(true);
}

/// <summary>
/// Creates all the specific customer promotions from a template.
/// </summary>
void clicked()
{
    TAMCopyTradePromotion   tamCopyTradePromotion;
    numberSeq               numberSeq;
    TAMPromotionID          promotionID;

    super();
    element.updateMerchandiseCost();

    numberSeq = NumberSeq::newGetNum(TAMPromotionParameters::numRefPromotionID());
    tamCopyTradePromotion = new TAMCopyTradePromotion(TAMTradePromotion);
    ttsBegin;
    promotionID = numberSeq.num();
    ttsCommit;

    tamCopyTradePromotion.copyToNewPromotion(promotionID,
                                                TAMTradePromotion.PromotionDescription,
                                                true, true, true, true, true, true,
                                                true, false);
    info("@MCR26160");
    TAMTradePromotion_ds.research();
}
