Method.Source:
[Form]
    /// <summary>
    /// The <c>TAMPromoStatistic</c> form calculates statistics for either a campaign or source code.
    /// </summary>
    public class TAMPromoStatistic extends FormRun
    {
        ProjTable           projTable;
        smmCampaignTable    campaignTable;
        MCRSourceCodeSetup  sourceCodeSetup;
        ProjTransIdBase     transId;
        TAMPromoStatistic   tamPromoStatistic;
        boolean             includeChildPromos;
        TAMPromotionStatisticGraph            graph;

}

void clearTmpPromoStatistic(boolean grayButton = false)
{
    select actual;
    if (actual)
    {
        actual.delete();
        actual.clear();
    }

    select budget;
    if (budget)
    {
        budget.delete();
        budget.clear();
    }

    select Deviation;
    if (Deviation)
    {
        Deviation.delete();
        Deviation.clear();
    }

    select TmpPromotionStatisticLink;
    if (TmpPromotionStatisticLink)
    {
        TmpPromotionStatisticLink.delete();
        TmpPromotionStatisticLink.clear();
    }

    ctrlTrans.enabled(grayButton);

    if (element.args().dataset() == tableNum(SMASubscriptionTable))
        ctrlForecast.enabled(false);
    else
        ctrlForecast.enabled(grayButton);

    TmpPromotionStatisticLink_ds.research();
}

/// <summary>
/// Initializes the form using either a campaign or a source code record.
/// </summary>
public void init()
{
    super();

    if (element.args().dataset() == tableNum(smmCampaignTable))
    {
        campaignTable = element.args().record();
        includeChildPromos = true;

        if (campaignTable.MCRSourceID)
        {
            sourceCodeSetup = MCRSourceCodeSetup::find(campaignTable.MCRSourceID);
        }
    }
    else if (element.args().dataset() == tableNum(MCRSourceCodeSetup))
    {
        sourceCodeSetup = element.args().record();
        select firstOnly campaignTable
            where campaignTable.MCRSourceID == sourceCodeSetup.SourceID;
    }
    else
    {
        throw error( strfmt("@SYS19096", element.name(), tablestr(smmCampaignTable) + '/' + tableStr(MCRSourceCodeSetup)));
    }

    if (campaignTable.ProjId)
    {
        projTable = ProjTable::find(campaignTable.ProjId);
    }

    element.setProject(projTable.RecId != 0);
    element.setSourceCode(sourceCodeSetup.RecId != 0);
    element.initGraph();
}

/// <summary>
/// Initializes the graph object.
/// </summary>
void initGraph()
{
    #macrolib.ChartFx
    // Initialize graph.
    graph = new TAMPromotionStatisticGraph();
    graph.parmSysChart(SysChart);
    graph.parmTmpDataSet(GraphicsTmpData);
    graph.create();

    graph.parmTitle("@MCR26448");
    graph.parmTitleYAxis("@SYS81541");

    OrderCountDisplay.value(NoYes::Yes);
    GrossMarginDisplay.value(NoYes::Yes);
    CostDisplay.value(NoYes::Yes);
    RevenueDisplay.value(NoYes::Yes);
}

TmpMCRPromotionStatistic insertDiviation(TmpMCRPromotionStatistic tmpActual, TmpMCRPromotionStatistic tmpBudget)
{
    TmpMCRPromotionStatistic tmpDiviation;

    tmpDiviation.PLPromoRevenue = tmpActual.PLPromoRevenue - tmpBudget.PLPromoRevenue;
    tmpDiviation.PLProjectRevenue = tmpActual.PLProjectRevenue - tmpBudget.PLProjectRevenue;
    tmpDiviation.PLPromoReturnRevenue = tmpBudget.PLPromoReturnRevenue - tmpActual.PLPromoReturnRevenue;

    tmpDiviation.PLCostPromo = tmpBudget.PLCostPromo - tmpActual.PLCostPromo;
    tmpDiviation.PLCostExpenses = tmpBudget.PLCostExpenses - tmpActual.PLCostExpenses;
    tmpDiviation.PLCostItem = tmpBudget.PLCostItem - tmpActual.PLCostItem;
    tmpDiviation.PLCostAccCost = tmpBudget.PLCostAccCost - tmpActual.PLCostAccCost;
    tmpDiviation.PLCostHour = tmpBudget.PLCostHour - tmpActual.PLCostHour;

    tmpDiviation.PLTotalRevenue = tmpActual.PLTotalRevenue - tmpBudget.PLTotalRevenue;
    tmpDiviation.PLCostTotalCost = tmpBudget.PLCostTotalCost - tmpActual.PLCostTotalCost;
    tmpDiviation.PLTotalGrossMargin = tmpActual.PLTotalGrossMargin - tmpBudget.PLTotalGrossMargin;
    tmpDiviation.ContributionRatio = tmpActual.ContributionRatio - tmpBudget.ContributionRatio;
    tmpDiviation.PromoOrderCount = tmpActual.PromoOrderCount - tmpBudget.PromoOrderCount;

    tmpDiviation.TransId = transId;
    tmpDiviation.ActualBudget = actualBudgetDeviation.selection();

    tmpDiviation.insert();

    return tmpDiviation;
}

/// <summary>
/// Sets the parameters for the <c>ProjStatistic</c> object so that we can pass
/// it to the project menu items called from the form.
/// </summary>
/// <returns>
/// The <c>ProjStatistic</c> object that was initialized.
/// </returns>
ProjStatistic projStatistic()
{
    ProjStatistic projStatistic;
    projStatistic = new ProjStatistic();

    projStatistic.parmFixedPrice(fixedPrice.value());
    projStatistic.parmTimeMaterial(timeMaterial.value());
    projStatistic.parmInternalCost(internalCost.value());
    projStatistic.parmInvestment(investment.value());
    projStatistic.parmTime(timeProject.value());

    projStatistic.parmHour(hour.value());
    projStatistic.parmExpenses(expenses.value());
    projStatistic.parmItem(item.value());
    projStatistic.parmRevenue(fee.value());
    projStatistic.parmOnAcc(onAcc.value());

    projStatistic.parmProjId(projTable.ProjId);
    projStatistic.parmProjTree(true);

    projStatistic.parmActualBudget(ActualBudgetDeviation.selection());

    return projStatistic;
}

/// <summary>
/// Initializes the <c>TAMPromoStatistic</c> object.
/// </summary>
/// <param name="_tmpPromotionStatistic">
/// The temporary promotion statistic buffer to use for initializing the the <c>TAMPromoStatistic</c> object.
/// </param>
/// <returns>
/// The initialized <c>TAMPromoStatistic</c> object.
/// </returns>
TAMPromoStatistic PromoStatistic(TmpMCRPromotionStatistic    _tmpPromotionStatistic)
{
    tamPromoStatistic = new TAMPromoStatistic(_tmpPromotionStatistic);

    tamPromoStatistic.parmFixedPrice(fixedPrice.value());
    tamPromoStatistic.parmTimeMaterial(timeMaterial.value());
    tamPromoStatistic.parmInternalCost(internalCost.value());
    tamPromoStatistic.parmInvestment(investment.value());
    tamPromoStatistic.parmTime(timeProject.value());

    tamPromoStatistic.parmHour(hour.value());
    tamPromoStatistic.parmExpenses(expenses.value());
    tamPromoStatistic.parmItem(item.value());
    tamPromoStatistic.parmRevenue(fee.value());
    tamPromoStatistic.parmOnAcc(onAcc.value());

    tamPromoStatistic.parmPromoSO(PromoSalesOrders.value());
    tamPromoStatistic.parmPromoCosts(PromoCosts.value());

    tamPromoStatistic.parmProjId(projTable.ProjId);
    tamPromoStatistic.parmChildPromo(includeChildPromos);
    tamPromoStatistic.parmCampaignId(campaignTable.CampaignId);
    tamPromoStatistic.parmSourceId(sourceCodeSetup.SourceID);

    tamPromoStatistic.parmActualBudget(actualBudgetDeviation.selection());

    return tamPromoStatistic;
}

/// <summary>
/// Loads the project budget deviation drop down manually.
/// </summary>
public void run()
{
    super();

    // Load drop down.
    ActualBudgetDeviation.clear();
    ActualBudgetDeviation.add(enum2str(ProjActualBudget::Actual));
    ActualBudgetDeviation.add(enum2str(ProjActualBudget::Budget));
    ActualBudgetDeviation.add(enum2str(ProjActualBudget::Deviation));
    // Set drop down to deviation.
    ActualBudgetDeviation.selection(ProjActualBudget::Deviation);
    ActualBudgetDeviation.modified();
}

