/// <summary>
/// Loads calculated values to graph.
/// </summary>
void setGraphValues()
{
    // Load graphics data and set titles
    graph.resetGraph();

    select actual;
    select budget;

    if (actualBudgetDeviation.selection() != ProjActualBudget::Budget)
    {
        if (RevenueDisplay.value())
        {
            graph.loadData("@SYS10074", "@SYS33313", actual.PLTotalRevenue);     // Actual Total Revenue
        }

        if (CostDisplay.value())
        {
            graph.loadData("@SYS4249", "@SYS33313", actual.PLCostTotalCost);     // Actual Cost
        }

        if (GrossMarginDisplay.value())
        {
            graph.loadData("@SYS78439", "@SYS33313", actual.PLTotalGrossMargin); // Actual Profit
        }

        if (OrderCountDisplay.value())
        {
            graph.loadData("@MCR26346", "@SYS33313", actual.PromoOrderCount);    // Actual Order Count
        }
    }

    if (actualBudgetDeviation.selection() != ProjActualBudget::Actual)
    {
        if (RevenueDisplay.value())
        {
            graph.loadData("@SYS10074", "@SYS125894", budget.PLTotalRevenue);     // Projected Revenue
        }

        if (CostDisplay.value())
        {
            graph.loadData("@SYS4249", "@SYS125894", budget.PLCostTotalCost);     // Projected Cost
        }

        if (GrossMarginDisplay.value())
        {
            graph.loadData("@SYS78439", "@SYS125894", budget.PLTotalGrossMargin); // Projected Profit
        }

        if (OrderCountDisplay.value())
        {
            graph.loadData("@MCR26346", "@SYS125894", budget.PromoOrderCount);    // Projected Order Count
        }
    }

    // Display the graph
    graph.showGraph();
}

