/// <summary>
/// Calculates the statistics based on the parameters the user has set.
/// </summary>
void updateButton()
{
    TmpMCRPromotionStatistic    tmpPromotionStatisticActual,
                                tmpPromotionStatisticBudget,
                                tmpPromotionStatisticDeviation;

    element.clearTmpPromoStatistic(true);

    TmpPromotionStatisticLink.ProjId = projTable.ProjId;
    TmpPromotionStatisticLink.insert();

    transId = TmpPromotionStatisticLink.TransId;

    // Calculate actual
    if (actualBudgetDeviation.selection() != ProjActualBudget::Budget)
    {
        element.PromoStatistic(actual);

        tamPromoStatistic.parmActual(true);
        tamPromoStatistic.parmTransId(transId);
        tamPromoStatistic.parmActualBudget(ProjActualBudget::Actual);
        tamPromoStatistic.calc();

        tmpPromotionStatisticActual.setTmpData(tamPromoStatistic.tmpMCRPromotionStatistic());
        select tmpPromotionStatisticActual;
        actual.setTmpData(tamPromoStatistic.tmpMCRPromotionStatistic());
    }

    // Calculate budget
    if (actualBudgetDeviation.selection() != ProjActualBudget::Actual)
    {
        element.PromoStatistic(budget);

        tamPromoStatistic.parmActual(false);
        tamPromoStatistic.parmTransId(transId);
        tamPromoStatistic.parmActualBudget(ProjActualBudget::Budget);
        tamPromoStatistic.calc();

        tmpPromotionStatisticBudget.setTmpData(tamPromoStatistic.tmpMCRPromotionStatistic());
        select tmpPromotionStatisticBudget;
        budget.setTmpData(tamPromoStatistic.tmpMCRPromotionStatistic());
    }

    // Update Deviation
    if (actualBudgetDeviation.selection() == ProjActualBudget::Deviation)
    {
        tmpPromotionStatisticDeviation = element.insertDiviation(tmpPromotionStatisticActual, tmpPromotionStatisticBudget);
        Deviation.setTmpData(tmpPromotionStatisticDeviation);
    }
}

