public void linkActive()
{
    ProjId  searchProjId;

    element.clearTmpPromoStatistic();
    element.setGraphValues();

    this.fixedPrice(false, false);
    this.timeMaterial(false, false);
    this.internalCost(false, false);
    this.investment(false, false);
    this.timeProject(false, false);

    this.hour(false, false);
    this.expenses(false, false);
    this.fee(false, false);
    this.onAccount(false, false);

    this.promoCosts(false, false);
    this.promoSalesOrders(true);

    if (element.args().dataset() == tableNum(smmCampaignTable))
    {
        campaignTable = element.args().record();
        includeChildPromos = true;
        sourceCodeSetup = NULL;

        if (campaignTable.MCRSourceID)
        {
            sourceCodeSetup = MCRSourceCodeSetup::find(campaignTable.MCRSourceID);
        }
    }
    else if (element.args().dataset() == tableNum(MCRSourceCodeSetup))
    {
        sourceCodeSetup = element.args().record();
        select firstOnly ProjId from campaignTable
            where campaignTable.MCRSourceID == sourceCodeSetup.SourceID;
    }

    projTable = NULL;

    if (campaignTable.ProjId)
    {
        projTable = ProjTable::find(campaignTable.ProjId);
    }

    if (projTable)
    {
        // If project exists then look for it as well as all its child projects and set
        // fields to calculate corresponding project costs & revenue.
        if (includeChildPromos)
        {
            searchProjId = projTable.ProjId + '*';
        }
        else
        {
            searchProjId = projTable.ProjId;
        }

        if ((select firstonly projTable
                where projTable.ProjId
                like searchProjId
                    && projTable.Type == ProjType::TimeMaterial).recid)
        {
            this.timeMaterial(true);

            this.fee(true);
            this.hour(true);
            this.expenses(true);
            this.onAccount(true);
        }

        if ((select firstonly projTable
                where projTable.ProjId like searchProjId
                    && projTable.Type == ProjType::FixedPrice).recid)
        {
            this.fixedPrice(true);

            this.fee(true);
            this.hour(true);
            this.expenses(true);

            this.onAccount(true);
        }

        if ((select firstonly projTable
                where projTable.ProjId
                like searchProjId
                    && projTable.Type == ProjType::Investment).recid)
        {
            this.investment(true);

            this.hour(true);
            this.expenses(true);
        }

        if ((select firstonly projTable
                where projTable.ProjId
                like searchProjId
                    && (projTable.Type == ProjType::Cost || projTable.Type == ProjType::Internal)).recid)
        {
            this.internalCost(true);

            this.hour(true);
            this.expenses(true);
        }

        if ((select firstonly projTable
                where projTable.ProjId
                like searchProjId
                    && projTable.Type == ProjType::Time).recid)
        {
            this.timeProject(true);
            this.hour(true);
        }
    }

    if (sourceCodeSetup.RecId != 0)
    {
        this.promoCosts(true);
    }

    element.setProject(projTable.RecId != 0);
    element.setSourceCode(sourceCodeSetup.RecId != 0);

    super();
}

