public boolean modified()
{
    boolean ret;

    ret = super();

    // When the drop down is changed by the user re-init the look of the form
    // and clear the previous calculations.
    switch (actualBudgetDeviation.selection())
    {
        case ProjActualBudget::Actual:
            element.setActual(true);
            element.setBudget(false);
            element.setDeviation(false);
            break;
        case ProjActualBudget::Budget:
            element.setActual(false);
            element.setBudget(true);
            element.setDeviation(false);
            break;
        case ProjActualBudget::Deviation:
            element.setActual(true);
            element.setBudget(true);
            element.setDeviation(true);
            break;
    }

    // element.setBudgetLabel( actualBudgetDeviation.selection()==ProjActualBudget::Budget );

    element.clearTmpPromoStatistic();
    element.setGraphValues();

    return ret;
}

