void calcQuantities()
{
    List                    listInventTrans;
    List                    listInvoiceId;
    ListEnumerator          listEnumerator;
    TAMActualQuantities     tamActualQuantities = new TAMActualQuantities(TAMTradePromotion);

    tamActualQuantities.calcInvoicedQty();

    shipQtyPromo = tamActualQuantities.getInvoicedQty();
    shipQtyPromo = shipQtyPromo;
    invAmtPromo = tamActualQuantities.getInvoiceAmt();

    // Go to the first element in the List.
    delete_from InventTransTemp;

    listInventTrans = List::create(tamActualQuantities::getInventTransTmpRecords(
            tamActualQuantities.getsalesorderinventTransId()));

    listEnumerator = listInventTrans.getEnumerator();

    while (listEnumerator.moveNext())
    {
        inventTransTemp.Data(listEnumerator.current());
        inventTransTemp.doinsert();
    }

    InventTransTemp_ds.executeQuery();

    delete_from CustInvoiceTrans;

    listInvoiceId = List::create(tamActualQuantities::getCustInvoiceTmpRecords(
            tamActualQuantities.getSalesOrderInvoiceRecId()));

    listEnumerator = listInvoiceId.getEnumerator();

    while (listEnumerator.moveNext())
    {
        CustInvoiceTrans.Data(listEnumerator.current());
        CustInvoiceTrans.doinsert();
    }

    CustInvoiceTrans_ds.executeQuery();
}

