/// <summary>
/// Updates the variables for summary used in display methods.
/// </summary>
void mcrCalculateSummarythisPromo()
{
    PdsRebateTrans          localPdsRebateTrans;
    PdsRebateTable          localPdsRebateTable;
    TAMMerchandisingEvent   tamMerchandsisingEvents;
    TAMMerchEventTrans      tamMerchEventTransLocal;
    TAMDeduction            tamDeductionLocal;

    deductionAmtPromo   = 0;
    lumpSumCreditAmtPromo    = 0;
    billBackCreditAmtPromo    = 0;
    invAmtPromo         = 0;

    select sum(Amount) from tamMerchEventTransLocal
        where tamMerchEventTransLocal.PromotionId == TAMTradePromotion.PromotionID;

    lumpSumCreditAmtPromo = tamMerchEventTransLocal.Amount;

    select sum(PdsRebateAmt) from localPdsRebateTrans
        where localPdsRebateTrans.PdsRebateTransaction != PdsRebateTransaction::LedgerJournalProcessing
        exists join localPdsRebateTable
            where localPdsRebateTable.PdsRebateId == localPdsRebateTrans.PdsRebateId
        exists join tamMerchandsisingEvents
            where tamMerchandsisingEvents.MerchandisingEventID == localPdsRebateTable.TAMMerchandisingEventID
                && tamMerchandsisingEvents.PromotionId == TAMTradePromotion.PromotionID;

    billBackCreditAmtPromo = localPdsRebateTrans.PdsRebateAmt;

    select sum(RemainAmountMST) from tamDeductionLocal
        where tamDeductionLocal.TAMPromotionID == TAMTradePromotion.PromotionID
            && tamDeductionLocal.TAMDeductionStatus == TAMDeductionStatus::Open;

    deductionAmtPromo = tamDeductionLocal.RemainAmountMST;

    element.calcQuantities();
}

