private TAMStatus mcrGetTAMStatus()
{
    // All does not map to the a tam status.
    if (tamStatus != TAMStatusFilter::All)
    {
        return enum2int(tamStatus);
    }
    else
    {
        throw error("@MCR39317");
    }
}

/// <summary>
/// Sets and resets the filters.
/// </summary>
/// <remarks>
/// All filters must also be added to deduction query in <c>MCRCalculateSummary</c>.
/// </remarks>
void mcrResetFilters()
{
    QueryBuildDataSource qbds = TAMTradePromotion_DS.queryBuildDataSource();

    if (promoOrderFrom)
    {
        promoOrderFrom.value(sysquery::valueUnlimited());
    }
    if (promoOrderTo)
    {
        promoOrderTo.value(sysquery::valueUnlimited());
    }

    switch (lastsel)
    {
        // order date
        case 0:
            promoOrderFrom = SysQuery::findOrCreateRange(qbds, fieldnum(TAMTradePromotion, OrdersStartDate));
            promoOrderTo = SysQuery::findOrCreateRange(qbds, fieldnum(TAMTradePromotion, OrdersEndDate));
            break;
        // requested ship date
        case 1:
            promoOrderFrom = SysQuery::findOrCreateRange(qbds, fieldnum(TAMTradePromotion, RequestedShipmentStartDate));
            promoOrderTo = SysQuery::findOrCreateRange(qbds, fieldnum(TAMTradePromotion, RequestedShipmentEndDate));
            break;
        // ship date
        case 2:
            promoOrderFrom = SysQuery::findOrCreateRange(qbds, fieldnum(TAMTradePromotion, ShipmentStartDate));
            promoOrderTo = SysQuery::findOrCreateRange(qbds, fieldnum(TAMTradePromotion, ShipmentEndDate));
            break;
        // performance date
        case 3:
            promoOrderFrom = SysQuery::findOrCreateRange(qbds, fieldnum(TAMTradePromotion, PerformanceStartDate));
            promoOrderTo = SysQuery::findOrCreateRange(qbds, fieldnum(TAMTradePromotion, PerformanceEndDate));
            break;
    }
}

/// <summary>
/// Searches promotions based on the filters.
/// </summary>
void mcrRunPromotionSearch()
{
    // reset filters
    this.mcrResetFilters();

    // promotion bock
    if (tamPromotionId)
    {
        promoTAMPromotionID.value(TAMPromotionId);
    }
    else
    {
        promoTAMPromotionID.value(sysquery::valueUnlimited());
    }

    // order date from
    if (!fromDate)
    {
        promoOrderFrom.value(sysquery::valueUnlimited());
    }
    else
    {
        promoOrderFrom.value(sysquery::value(fromDate)+'..');
    }

    // order date to
    if (!todate)
    {
        promoOrderTo.value(sysquery::valueUnlimited());
    }
    else
    {
        promoOrderTo.value('..'+sysQuery::value(toDate));
    }
    // Promotion Status
    if (tamStatus != TAMStatusFilter::All)
    {
        promoStatus.value(sysquery::value(element.mcrGetTAMStatus()));
    }
    else
    {
        promoStatus.value(SysQuery::valueUnlimited());
    }

    TAMTradePromotion_ds.executeQuery();
}

public void run()
{
    super();

    tamStatus = TAMStatusFilter::All;
    PromotionStatus.selection(tamStatus);

    this.mcrRunPromotionSearch();
}

