public void init()
{
    super();

    QueryBuildDataSource qbds = TAMTradePromotion_DS.queryBuildDataSource();

    //qbds.addRange(fieldnum(TAMTradePromotion, PromotionStatus)).value(SysQuery::value(TAMStatus::CustomerApproved));
    qbds.addRange(fieldnum(TAMTradePromotion, Template)).value(SysQuery::value(NoYes::No));
    qbds.addRange(fieldnum(TAMTradePromotion, Activity)).value(enum2str(NoYes::Yes));

    promoTAMPromotionID = qbds.addRange(fieldnum(TAMTradePromotion, PromotionID));
    promoStatus = qbds.addRange(fieldnum(TAMTradePromotion, PromotionStatus));
}

public void executeQuery()
{
    QueryBuildDataSource    queryBuildDataSourceMerchEvents;
    Query                   query;

    query = this.query();
    queryBuildDataSourceMerchEvents = query.dataSourceTable(tableNum(TAMMerchandisingEvent));
    queryBuildDataSourceMerchEvents.clearRanges();

    queryBuildDataSourceMerchEvents.addRange(fieldNum(TAMMerchandisingEvent, PromotionCust)).value(TAMTradePromotion.custAccount);

    super();

    PdsRebateTrans_ds.executeQuery();
}

