public void init()
{
    QueryBuildDataSource    dataSourceMerchEvent;
    QueryBuildDataSource    dataSourcePromotion;
    QueryBuildDataSource    dataSourceRebateTable;
    QueryBuildDataSource    dataSourceRebateTrans;
    QueryBuildRange         rangeRebateTrans;

    super();

    dataSourceRebateTrans = PdsRebateTrans_ds.query().dataSourceName(PdsRebateTrans_ds.name());
    dataSourceRebateTrans.clearRanges();
    dataSourceRebateTrans.clearLinks();
    dataSourceRebateTrans.clearDynalinks();

    rangeRebateTrans = dataSourceRebateTrans.addRange(fieldNum(PdsRebateTrans, PdsRebateTransaction));
    rangeRebateTrans.value(SysQuery::valueNot(PdsRebateTransaction::LedgerJournalProcessing));

    dataSourceRebateTable = dataSourceRebateTrans.addDataSource(tableNum(PdsRebateTable));
    dataSourceRebateTable.joinMode(JoinMode::InnerJoin);
    dataSourceRebateTable.addLink(fieldNum(PdsRebateTrans, PdsRebateId),
        fieldNum(PdsRebateTable, PdsRebateId));

    dataSourceMerchEvent = dataSourceRebateTable.addDataSource(tableNum(TAMMerchandisingEvent));
    dataSourceMerchEvent.joinMode(JoinMode::InnerJoin);
    dataSourceMerchEvent.addLink(fieldNum(PdsRebateTable, TAMMerchandisingEventId),
        fieldNum(TAMMerchandisingEvent, MerchandisingEventId));

    dataSourcePromotion = dataSourceMerchEvent.addDataSource(tableNum(TAMTradePromotion));
    dataSourcePromotion.joinMode(JoinMode::InnerJoin);
    dataSourcePromotion.addLink(fieldNum(TAMMerchandisingEvent, PromotionId),
        fieldNum(TAMTradePromotion, PromotionId));
    if (dataSourceRebateTable.findRange(fieldNum(TAMTradePromotion, PromotionId)))
    {
        dataSourceRebateTable.clearRange(fieldNum(TAMTradePromotion, PromotionId));
    }
    promotionIdRangePayment = dataSourcePromotion.addRange(fieldNum(TAMTradePromotion, PromotionId));

    this.cacheAddMethod(tableMethodStr(PdsRebateTrans,tamDisplayCustAccount));
    this.cacheAddMethod(tableMethodStr(PdsRebateTrans,tamDisplayProcessDate));
    this.cacheAddMethod(tableMethodStr(PdsRebateTrans,tamDisplayCurrencyCode));
}

public void executeQuery()
{
    promotionIdRangePayment.value(TAMTradePromotion.PromotionID);
    super();
}

public void jumpRef()
{
    CustInvoiceJour custInvoiceJour;
    Args            args;
    MenuFunction    menuFunction;

    select firstOnly custInvoiceJour
        where custInvoiceJour.InvoiceId == TAMMerchEventTrans.CustInvoiceNum;

    args = new Args();
    args.caller(element);
    args.record(custInvoiceJour);
    menuFunction = new MenuFunction(
    menuitemdisplaystr(CustInvoiceJournal),
    MenuItemType::Display);
    menuFunction.run(args);
}

