void clicked()
{
    container       parameters;
    FormRun         formRun;
    ContainerClass  conClass;

    Args        args = new Args();

    parameters = conIns(parameters, 1, tamPromotionID);
    parameters = conIns(parameters, 2, fromDate);
    parameters = conIns(parameters, 3, toDate);

    conClass = new ContainerClass(parameters);
    args.parmObject(conClass);
    args.parmEnum(enum2int(tamStatus));
    args.parmEnumType(EnumNum(TAMStatusFilter));
    args.caller(this);

    formRun = new MenuFunction(menuitemDisplayStr(TAMPromotionAnalysisSummary), MenuItemType::Display).create(args);
    if (formRun)
    {
        formRun.run();
        formRun.wait();
    }
}

