[Form]
public class TAMTradePromotions extends FormRun implements OfficeIGeneratedWorkbookCustomExporter, OfficeIMenuCustomizer
{
    private const str ExportTradeAllowanceAgreementCustom = 'TAMTradeAllowanceAgreementCustomMenu';


    InventDimCtrl_Frm_TAM inventDimFormSetupHeader;
    InventDimCtrl_Frm_TAM inventDimFormSetupLine;
    InventDimCtrl_Frm_TAM currentInventDimFormSetup;

    FormListControl                         custImageList;

    NumberSeqFormHandler                    numberSeqFormHandler;
    NumberSeqFormHandler                    numberSeqFormHandlerPromotionId;

    TAMPromotionMode                        formMode;

    HcmWorkerLookup                         hcmWorkerLookupCurrentCompany;

    MCRCustCategoryTreeDatasource           mcrCustTreeDatasourceTradePromo;
    MCRCustCategoryHierarchy                mcrCurCustHierarchyTradePromo;

    boolean                                 workflowEnabled;

}

public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    List exportOptions = _menuOptions.customMenuItems();

    var exportOption = OfficeGeneratedExportMenuItem::construct(tableStr(TAMTradeAllowanceAgreementHeaderEntity), ExportTradeAllowanceAgreementCustom);
    exportOption.displayName("@SCM:TradeAllowanceAgreementHeaderAndLines");
    exportOptions.addEnd(exportOption);
}

public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = null;

    switch (_menuItem.id())
    {
        case ExportTradeAllowanceAgreementCustom:
            context = ExportToExcelDataEntityContext::constructDefault(tableStr(TAMTradeAllowanceAgreementHeaderEntity));

            var headerEntityInfo = context.findEntityByName(tableStr(TAMTradeAllowanceAgreementHeaderEntity));

            var headerFilterBuilder = new ExportToExcelFilterTreeBuilder(tablestr(TAMTradeAllowanceAgreementHeaderEntity));
            var headerAgreementIdFilter = headerFilterBuilder.areEqual(fieldStr(TAMTradeAllowanceAgreementHeaderEntity, AgreementId), TAMTradePromotion.PromotionID);
            headerEntityInfo.filter(headerFilterBuilder.and(headerFilterBuilder.companyFilter(), headerAgreementIdFilter));

            element.addContext(context,
                               tableStr(TAMTradeAllowanceAgreementProductEntity),
                               fieldStr(TAMTradeAllowanceAgreementProductEntity, TradeAllowanceAgreementId),
                               TAMTradePromotion.PromotionID);

            element.addContext(context,
                               tableStr(TAMTradeAllowanceAgreementFundEntity),
                               fieldStr(TAMTradeAllowanceAgreementFundEntity, TradeAllowanceAgreementId),
                               TAMTradePromotion.PromotionID);

            element.addContext(context,
                               tableStr(TAMTradeAllowanceAgreementMerchandisingEventEntity),
                               fieldStr(TAMTradeAllowanceAgreementMerchandisingEventEntity, TradeAllowanceAgreementId),
                               TAMTradePromotion.PromotionID);

            element.addContext(context,
                               tableStr(TAMTradeAllowanceAgreementMerchandisingEventProductEntity),
                               fieldStr(TAMTradeAllowanceAgreementMerchandisingEventProductEntity, TradeAllowanceAgreementId),
                               TAMTradePromotion.PromotionID);

            element.addContext(context,
                               tableStr(TAMTradeAllowanceAgreementMerchandisingEventFundEntity),
                               fieldStr(TAMTradeAllowanceAgreementMerchandisingEventFundEntity, TradeAllowanceAgreementId),
                               TAMTradePromotion.PromotionID);

            element.addContext(context,
                               tableStr(TAMTradeAllowanceAgreementMerchandisingEventDiscountEntity),
                               fieldStr(TAMTradeAllowanceAgreementMerchandisingEventDiscountEntity, TradeAllowanceAgreementId),
                               TAMTradePromotion.PromotionID);

            element.addContext(context,
                               tableStr(TAMTradeAllowanceAgreementMerchandisingEventLumpSumEntity),
                               fieldStr(TAMTradeAllowanceAgreementMerchandisingEventLumpSumEntity, TradeAllowanceAgreementId),
                               TAMTradePromotion.PromotionID);

            element.addContext(context,
                               tableStr(TAMTradeAllowanceAgreementMerchandisingEventBillBackEntity),
                               fieldStr(TAMTradeAllowanceAgreementMerchandisingEventBillBackEntity, TradeAllowanceAgreementId),
                               TAMTradePromotion.PromotionID);

            element.addContext(context,
                               tableStr(TAMTradeAllowanceAgreementMerchandisingEventBillBackLineEntity),
                               fieldStr(TAMTradeAllowanceAgreementMerchandisingEventBillBackLineEntity, TradeAllowanceAgreementId),
                               TAMTradePromotion.PromotionID);
            break;
    }

    return context;
}

private void addContext(ExportToExcelDataEntityContext _context,
                              TableName                _entityName,
                              FieldName                _filterField,
                              TAMPromotionId           _filterValuePromotionId)
{
    var entityInfo = _context.addEntityDefault(_entityName);

    var filterBuilder = new ExportToExcelFilterTreeBuilder(_entityName);
    var agreementIdFilter = filterBuilder.areEqual(_filterField, _filterValuePromotionId);
    entityInfo.filter(filterBuilder.and(filterBuilder.companyFilter(), agreementIdFilter));
}

public boolean canActivateApprovalWorkflow(TableId tableId, RecId recId)
{
    WorkflowVersionTable versionTable = WorkflowTable::findActiveTemplate(workFlowTypeStr(TAMPromoMgmtApproval)).getActiveVersion();
    SysDictWorkflowType     sysDictWorkflowTemplate;
    sysDictWorkflowTemplate = new SysDictWorkflowType(workFlowTypeStr(TAMPromoMgmtApproval));

    if (versionTable.ActivationConditionType == NoYes::Yes
        && sysDictWorkflowTemplate.disableActivationConditions() == false)
    {
        return Workflow::evaluateWorkflowActivationCondition(versionTable, recId, tableId);
    }

    //if there is not activation condition set up, allow activation by default
    return true;
}

public boolean canClose()
{
    boolean ret;

    ret = super();

    if (!element.closedOk() && numberSeqFormHandler)
    {
        numberSeqFormHandler.formMethodClose();
    }

    if (!element.closedOk() && numberSeqFormHandlerPromotionId)
    {
        numberSeqFormHandlerPromotionId.formMethodClose();
    }

    return ret;
}

public boolean canSubmitToWorkflow()
{
    boolean ret;

    ret = super();

    if (ret && tamTradePromotion.RecId)
    {
        ret = element.canActivateApprovalWorkflow(tableNum(TAMTradePromotion), tamTradePromotion.RecId);
    }

    return ret;
}

/// <summary>
/// Locks down all lump sum fields except payout notes, based on status.
/// </summary>
void disableLumpSumByStatus()
{
    boolean enabled;

    enabled = tamTradePromotion.PromotionStatus == TAMStatus::CustomerApproved
                && tamMerchEventPricing.LumpSumStatus != MCRLumpSumStatus::Decline
                && !tamTradePromotion.Template;

    lumpSum_MCRLumpSumStatus.enabled(enabled);
    lumpSum_LumpSumApprovedAmount.enabled(enabled);
    approveReject.enabled(enabled);
    approveRejectActionPane.enabled(enabled);
}

/// <summary>
/// Locks down everything on the trade promotions form based on status.
/// </summary>
void enableByStatus()
{
    boolean enable;
    boolean enableByDate;

    if (tamTradePromotion.PromotionStatus == TAMStatus::InProcess)
    {
        // If workflow is enabled and is in the "In Review" status, don't enable the controls.
        if (!workflowEnabled || (workflowEnabled
            && tamTradePromotion.wfApprovalStatus != TAMPromoMgmtApprovalStatus::Submitted))
        {
            enable = true;
        }
    }

    if (tamTradePromotion.OrdersEndDate >= DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone())
        || tamTradePromotion.OrdersEndDate == dateNull())
    {
        enableByDate = true;
    }

    tamTradePromotion_ds.object(fieldNum(TAMTradePromotion, PromotionID)).enabled(enable);
    tamTradePromotion_ds.object(fieldNum(TAMTradePromotion, PromotionDescription)).enabled(enable);
    tamTradePromotion_ds.object(fieldNum(TAMTradePromotion, Currency)).enabled(enable);
    tamTradePromotion_ds.object(fieldNum(TAMTradePromotion, MarketingObjective)).enabled(enable);
    tamTradePromotion_ds.object(fieldNum(TAMTradePromotion, HcmWorkerId)).enabled(enable);
    tamTradePromotion_ds.object(fieldNum(TAMTradePromotion, PromotionDetails)).enabled(enable);

    referenceGroup.enabled(enable);
    lineViewHeaderReferenceGroup.enabled(enable);
    headerGridReferenceGroup.enabled(enable);

    dates.enabled(enable);
    lineViewHeaderDates.enabled(enable);

    statistics.enabled(enable);

    tamMerchandisingEvent_ds.allowCreate(enable && enableByDate);
    tamPromotionFundME_ds.allowCreate(enable && enableByDate);
    tamPromotionFundTP_ds.allowCreate(enable);
    tamMerchEventPricing_ds.allowCreate(enable && enableByDate);
    inventDimME_ds.allowCreate(enable && enableByDate);
    pdsRebateAgreement_ds.allowCreate(enable && enableByDate);
    pdsRebateAgreementLine_ds.allowCreate(enable && enableByDate);

    tamMerchandisingEvent_ds.allowEdit(enable && enableByDate);
    tamPromotionItemME_ds.allowEdit(enable);
    inventDimTP_ds.allowEdit(enable);
    tamPromotionItemTP_ds.allowEdit(enable);
    tamPromotionFundTP_ds.allowEdit(enable);
    tamPromotionFundME_ds.allowEdit(enable && enableByDate);
    inventDimME_ds.allowEdit(enable && enableByDate);
    pdsRebateAgreement_ds.allowEdit(enable && enableByDate);
    pdsRebateAgreementLine_ds.allowEdit(enable && enableByDate);
    limitGroup.enabled(enable && enableByDate);

    tamTradePromotion_ds.allowDelete(enable);
    tamMerchandisingEvent_ds.allowDelete(enable && enableByDate);
    tamPromotionItemME_ds.allowDelete(enable && enableByDate);
    inventDimTP_ds.allowDelete(enable);
    tamPromotionItemTP_ds.allowDelete(enable);
    tamPromotionFundTP_ds.allowDelete(enable);
    tamPromotionFundME_ds.allowDelete(enable && enableByDate);
    tamMerchEventPricing_ds.allowDelete(enable && enableByDate);
    inventDimME_ds.allowDelete(enable && enableByDate);
    pdsRebateAgreement_ds.allowDelete(enable && enableByDate);
    pdsRebateAgreementLine_ds.allowDelete(enable && enableByDate);

    TPCustBtnGrp.enabled(enable);
    itemsMETAMRetailAddItemsButton.enabled(enable && enableByDate);
    TAMPromotionItemME_DS.allowCreate(enable && enableByDate);
    deleteLineGridCmdButton.enabled(enable && enableByDate);
    tamRetailAddItemsButton.enabled(enable);
    TAMPromotionItemTP_DS.allowCreate(enable);

    LumpSumDetails.enabled(enable);
    PaymentDetails.enabled(enable);
    payout.enabled(!enable);
    approveReject.enabled(!enable);
    approveRejectActionPane.enabled(!enable);
    discount.enabled(enable);
    discountAddRemoveButton.enabled(enable && enableByDate);
    billBackRebateAgreementLineGroup.enabled(enable && enableByDate);

    element.enableMerchEventButtons();
    element.disableLumpSumByStatus();
    tamTradePromotion_ds.refresh();
}

public void enableMerchEventButtons()
{
    if (tamMerchandisingEvent.MerchandisingEventID != ''
        && tamTradePromotion.PromotionStatus == TAMStatus::InProcess
        && (tamTradePromotion.OrdersEndDate >= DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone())
            || tamTradePromotion.OrdersEndDate == dateNull()))
    {
        element.enableMerchEventFormControls(true);
    }
    else
    {
        element.enableMerchEventFormControls(false);
    }
}

/// <summary>
/// Enables or disables form controls based on the passed parameters.
/// </summary>
/// <param name="_merchEventEnabled">
/// A boolean value that defines whether the form controls should be enabled or not.
/// </param>
public void enableMerchEventFormControls( boolean _merchEventEnabled)
{
    itemsMETAMRetailAddItemsButton.enabled(_merchEventEnabled);
    TAMPromotionItemME_DS.allowCreate(_merchEventEnabled);
    removeItemME.enabled(_merchEventEnabled);
    deleteLineGridCmButtomFunds.enabled(_merchEventEnabled);
    newLineCommandButtonFunds.enabled(_merchEventEnabled);

    tamMerchEventPricing_ds.allowCreate(_merchEventEnabled);
    tamMerchEventPricing_ds.allowDelete(_merchEventEnabled);

    pdsRebateAgreementLine_ds.allowCreate(_merchEventEnabled);
    pdsRebateAgreementLine_ds.allowEdit(_merchEventEnabled);
    pdsRebateAgreementLine_ds.allowDelete(_merchEventEnabled);
}

/// <summary>
/// Enables the line break fields on the rebate agreement lines.
/// </summary>
public void enableRebateAgreementLineFields()
{
    if (pdsRebateAgreement.PdsRebateLineBreakType == PdsRebateLineBreakType::Amount)
    {
        pdsRebateAgreementLine_RebateFromQty.visible(false);
        pdsRebateAgreementLine_RebateToQty.visible(false);
        pdsRebateAgreementLine_RebateFromAmount.visible(true);
        pdsRebateAgreementLine_RebateToAmount.visible(true);
    }
    else if (pdsRebateAgreement.PdsRebateLineBreakType == PdsRebateLineBreakType::Quantity)
    {
        pdsRebateAgreementLine_RebateFromQty.visible(true);
        pdsRebateAgreementLine_RebateToQty.visible(true);
        pdsRebateAgreementLine_RebateFromAmount.visible(false);
        pdsRebateAgreementLine_RebateToAmount.visible(false);
    }
}

/// <summary>
/// Enables and disables trade allowance promotion status buttons.
/// </summary>
void enableStatusButtons()
{
    switch (tamTradePromotion.PromotionStatus)
    {
        case TAMStatus::InProcess:
            if (workflowEnabled
                && tamTradePromotion.wfApprovalStatus == TAMPromoMgmtApprovalStatus::Approved)
            {
                inProcessButton.enabled(false);
                internalApprovedButton.enabled(true);
                confirmedButton.enabled(true);
                closedButton.enabled(false);
            }
            else if (workflowEnabled)
            {
                inProcessButton.enabled(false);
                internalApprovedButton.enabled(false);
                confirmedButton.enabled(false);
                closedButton.enabled(false);
            }
            else
            {
                inProcessButton.enabled(false);
                internalApprovedButton.enabled(true);
                confirmedButton.enabled(true);
                closedButton.enabled(true);
            }
            break;
        case TAMStatus::ManagementApproved:
            inProcessButton.enabled(true);
            internalApprovedButton.enabled(false);
            confirmedButton.enabled(true);
            closedButton.enabled(true);
            break;
        case TAMStatus::CustomerApproved:
            inProcessButton.enabled(!tamTradePromotion.checkMerchEventTrans(tamTradePromotion.PromotionID));
            internalApprovedButton.enabled(false);
            confirmedButton.enabled(false);
            closedButton.enabled(true);
            break;
        case TAMStatus::Closed:
            inProcessButton.enabled(!tamTradePromotion.checkMerchEventTrans(tamTradePromotion.PromotionID));
            internalApprovedButton.enabled(false);
            confirmedButton.enabled(false);
            closedButton.enabled(false);
            break;
    }
}

/// <summary>
/// Enables the vendor pay to field based on the rebate payment type.
/// </summary>
void enableTAMVendPaymTo()
{
    pdsRebateAgreement_TAMVendPayTo.enabled(pdsRebateAgreement.tamPaymentType == TAMPaymentType::Cheque);
    pdsRebateAgreement_TAMVendPayTo.skip(pdsRebateAgreement.tamPaymentType != TAMPaymentType::Cheque);
}

/// <summary>
/// Initializes the form variables based on the form from which it is being called.
/// </summary>
public void init()
{
    TAMPromotionParameters  tamPromotionParameters = TAMPromotionParameters::find();

    // When coming from deduction set mode to customer.
    if (element.args().dataset() == tableNum(TAMDeduction))
    {
        element.args().parmEnumeration(TAMPromotionMode::Customer);
    }

    if (element.args().parmEnumeration() == TAMPromotionMode::Customer)
    {
        formMode = TAMPromotionMode::Customer;
    }
    else
    {
        formMode = TAMPromotionMode::Template;
    }

    super();

    workflowEnabled = WorkflowTable::findActiveTemplate(workFlowTypeStr(TAMPromoMgmtApproval)).RecId != 0;

    element.design().workflowEnabled(workflowEnabled);

    hcmWorkerLookupCurrentCompany = HcmWorkerLookup::newOnlyActiveEmployeesWithinCompany();

    if (!mcrCustCategoryHierarchy_Name.valueStr())
    {
        mcrCustCategoryHierarchy_Name.text(tamPromotionParameters.DefaultCustHierarchy);
        mcrCurCustHierarchyTradePromo = MCRCustCategoryHierarchy::findByName(tamPromotionParameters.DefaultCustHierarchy);
        element.initializeHelperTP_CustHeader();
    }

    element.initDisplay();
    element.updateDesignHeader(InventDimFormDesignUpdate::Init);
    element.updateDesignLine(InventDimFormDesignUpdate::Init);
    currentInventDimFormSetup = inventDimFormSetupHeader;
    element.setDimensionFields();
    tamTradePromotion_ds.allowCreate(true);

    element.enableMerchEventFormControls(false);
}

/// <summary>
/// Initializes the form display based on whether the form is being called
/// for template useage or for specific customer promotions.
/// </summary>
void initDisplay()
{
    if (formMode == TAMPromotionMode::Customer)
    {
        element.design().caption("@MCR25034");
        tamTradePromotion.Template = true;
    }
    else
    {
        // Change title to say trade promotion templates.
        element.design().caption("@MCR26100");

        tabPageHeaderCustomers.visible(true);

        tamTradePromotion_ds.object(fieldNum(TAMTradePromotion, PromotionStatus)).visible(false);
        tamTradePromotion_ds.object(fieldNum(TAMTradePromotion, Activity)).visible(false);

        createIndividualPromotions.visible(true);
        plannedAnalysis.visible(false);
        statusGroup.visible(false);
    }
}

/// <summary>
/// Initializes the object used to control the customer category tree object.
/// </summary>
void initializeHelperTP_Cust()
{
    QueryBuildRange                 range;
    MCRCustCategory                  root;

    range = mcrCustCategoryHierarchy_ds.queryBuildDataSource().addRange(fieldNum(MCRCustCategoryHierarchy, RecId));
    range.value( queryValue(mcrCurCustHierarchyTradePromo.RecId ) );
    mcrCustCategoryHierarchy_ds.executeQuery();

    range = mcrCustCategory_ds.queryBuildDataSource().addRange(fieldNum(MCRCustCategory, CategoryHierarchy));
    range.value( queryValue(mcrCurCustHierarchyTradePromo.RecId ) );
    mcrCustCategory_ds.executeQuery();

    root = MCRCustCategory::getRoot(mcrCurCustHierarchyTradePromo.RecId);

    if (root)
    {
        mcrCustTreeDatasourceTradePromo = new MCRCustCategoryTreeDatasource(mcrCustCategory_ds,
                                                                            lineViewHeaderCtrlCustTree,
                                                                            fieldNum(MCRCustCategory, RecId),
                                                                            fieldNum(MCRCustCategory, ParentCategory),
                                                                            fieldNum(MCRCustCategory, Name),
                                                                            false,
                                                                            true,
                                                                            fieldNum(MCRCustCategory, Code),
                                                                            mcrCurCustHierarchyTradePromo,
                                                                            fieldNum(MCRCustCategory, NestedSetLeft) );

        mcrCustTreeDatasourceTradePromo.initRoot(root.Name, root.RecId, mcrCustTreeDatasourceTradePromo.image());
    }
}

/// <summary>
/// Initializes the object used to control the customer category tree object.
/// </summary>
void initializeHelperTP_CustHeader()
{
    QueryBuildRange         range;
    MCRCustCategory         root;

    range = mcrCustCategoryHierarchy_ds.queryBuildDataSource().addRange(fieldNum(MCRCustCategoryHierarchy, RecId));
    range.value( queryValue(mcrCurCustHierarchyTradePromo.RecId ) );
    mcrCustCategoryHierarchy_ds.executeQuery();

    range = mcrCustCategory_ds.queryBuildDataSource().addRange(fieldNum(MCRCustCategory, CategoryHierarchy));
    range.value( queryValue(mcrCurCustHierarchyTradePromo.RecId ) );
    mcrCustCategory_ds.executeQuery();

    root = MCRCustCategory::getRoot(mcrCurCustHierarchyTradePromo.RecId);

    if (root)
    {
        mcrCustTreeDatasourceTradePromo = new MCRCustCategoryTreeDatasource(mcrCustCategory_ds,
                                                                            ctrlCustTree,
                                                                            fieldNum(MCRCustCategory, RecId),
                                                                            fieldNum(MCRCustCategory, ParentCategory),
                                                                            fieldNum(MCRCustCategory, Name),
                                                                            false,
                                                                            true,
                                                                            fieldNum(MCRCustCategory, Code),
                                                                            mcrCurCustHierarchyTradePromo,
                                                                            fieldNum(MCRCustCategory, NestedSetLeft) );

        mcrCustTreeDatasourceTradePromo.initRoot(root.Name, root.RecId, mcrCustTreeDatasourceTradePromo.image());
    }
}

