    inventoryDimensions.enabled(true);
}

public void setRebateAgreementStatus()
{
    tamTradePromotion.setRebateAgreementStatus();
    tamTradePromotion_ds.research(true);
    pdsRebateAgreement_ds.reread();
    pdsRebateAgreement_ds.refresh();
}

/// <summary>
/// Sets the display properties of the objects on the discount tab based
/// on the merchandising event type.
/// </summary>
void showBillBackFields()
{
    if (tamMerchandisingEvent.MerchandisingEventCategory == MCRMerchandisingEventCategory::BillBack)
    {
        billBackDefinition.visible(true);
        approveRejectActionPane.visible(false);
    }
    else
    {
        billBackDefinition.visible(false);
    }
}

/// <summary>
/// Hides and shows the customer tab based on
/// whether or not we are looking at templates.
/// </summary>
void showCustomersTab()
{
    if (tamTradePromotion.Template)
    {
        tabPageHeaderCustomers.visible(true);
    }
    else
    {
        tabPageHeaderCustomers.visible(false);
    }
}

/// <summary>
/// Sets the display properties of the objects on the discount tab based
/// on the merchandising event type.
/// </summary>
void showMerchandisingEventCategoryFields()
{
    if (tamMerchandisingEvent.MerchandisingEventCategory == MCRMerchandisingEventCategory::LumpSum)
    {
        lumpSum.visible(true);
        approveRejectActionPane.visible(true);
        discounts.visible(false);
        billBackDefinition.visible(false);
        billBackRebateAgreementLineGroup.visible(false);
    }
    else if (tamMerchandisingEvent.MerchandisingEventCategory == MCRMerchandisingEventCategory::BillBack)
    {
        lumpSum.visible(false);
        approveRejectActionPane.visible(false);
        discounts.visible(false);
        element.showBillBackFields();
        billBackDefinition.visible(true);
        billBackRebateAgreementLineGroup.visible(true);
    }
    else
    {
        lumpSum.visible(false);
        approveRejectActionPane.visible(false);
        discounts.visible(true);
        billBackDefinition.visible(false);
        billBackRebateAgreementLineGroup.visible(false);
    }
}

/// <summary>
/// Updates the design display for the inventory dimension fields for the header.
/// </summary>
/// <param name="mode">
/// The design update mode for the inventory dimension fields.
/// </param>
void updateDesignHeader(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetupHeader)
            {
                Set headerDataSources = new Set(Types::String);
                headerDataSources.add(TAMTradePromotion_DS.name());
                headerDataSources.add(InventDimTP_DS.name());
                headerDataSources.add(TAMPromotionItemTP_DS.name());
                headerDataSources.add(TAMPromotionFundTP_DS.name());
                headerDataSources.add(TAMPromotionCust_DS.name());
                headerDataSources.add(CustTable_DS.name());
                inventDimFormSetupHeader = InventDimCtrl_Frm_TAM::newFromForm(element, headerDataSources);
            }
        case InventDimFormDesignUpdate::Active:
            inventDimFormSetupHeader.formActiveSetup(InventDimGroupSetup::newItemIdProductDimensionsOnly(tamPromotionItemTP.ItemID));
            inventDimFormSetupHeader.formSetControls(true);
            break;
        case InventDimFormDesignUpdate::FieldChange:
            inventDimFormSetupHeader.formActiveSetup(InventDimGroupSetup::newItemIdProductDimensionsOnly(tamPromotionItemTP.ItemID));
            inventDimFormSetupHeader.formSetControls(false);
            break;
        default: throw error(strFmt("@SYS54195", funcName()));
    }
}

/// <summary>
/// Updates the design display for the inventory dimension fields for the lines.
/// </summary>
/// <param name="mode">
/// The design update mode for the inventory dimension fields.
/// </param>
void updateDesignLine(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetupLine)
            {
                Set lineDataSources = new Set(Types::String);
                lineDataSources.add(TAMMerchandisingEvent_DS.name());
                lineDataSources.add(TAMPromotionItemME_DS.name());
                lineDataSources.add(TAMPromotionFundME_DS.name());
                lineDataSources.add(TAMMerchEventPricing_DS.name());
                lineDataSources.add(InventDimME_DS.name());
                lineDataSources.add(PdsRebateAgreement_DS.name());
                lineDataSources.add(PdsRebateAgreementLine_DS.name());
                inventDimFormSetupLine = InventDimCtrl_Frm_TAM::newFromForm(element, lineDataSources);
            }
        case InventDimFormDesignUpdate::Active:
            inventDimFormSetupLine.formActiveSetup(InventDimGroupSetup::newItemIdProductDimensionsOnly(tamPromotionItemME.ItemID));
            inventDimFormSetupLine.formSetControls(true);
            break;
        case InventDimFormDesignUpdate::FieldChange:
            inventDimFormSetupLine.formActiveSetup(InventDimGroupSetup::newItemIdProductDimensionsOnly(tamPromotionItemME.ItemID));
            inventDimFormSetupLine.formSetControls(false);
            break;
        default: throw error(strFmt("@SYS54195", funcName()));
    }
}

public void updateMerchandiseCost()
{
    TAMCalcMerchandiseCost tamCalcMerchandiseCost;

    // Calculate merchandise cost.
    tamCalcMerchandiseCost = new TAMCalcMerchandiseCost(TAMPromoMerchEvent::Promotion,
                                                            '',
                                                            tamTradePromotion.PromotionID,
                                                            tamTradePromotion.CustAccount);
    tamCalcMerchandiseCost.calcPromotionCost();
    tamTradePromotion_ds.reread();
    tamTradePromotion_ds.refreshEx();
    tamMerchandisingEvent_ds.executeQuery();
}

/// <summary>
/// Sets PdsPaymtType of <c>PdsRebateAgreement</c>
/// </summary>
protected void setRebateAgreementPaymtType()
{
    switch (pdsRebateAgreement.tamPaymentType)
    {
        case TAMPaymentType::CustomerDeduction:
            pdsRebateAgreement.PdsPaymtType = PdsPaymtType::CustomerDeduction;
            break;
        case TAMPaymentType::InvoiceCustomerDeduction:
            pdsRebateAgreement.PdsPaymtType = PdsPaymtType::InvoiceCustomerDeduction;
            break;
        case TAMPaymentType::Cheque:
            pdsRebateAgreement.PdsPaymtType = PdsPaymtType::Cheque;
            break;
    }
}

public void delete()
{
    super();

    element.enableByStatus();
}

public display String30 displayClaimPayType(TAMMerchandisingEvent _tamMerchandisingEvent)
{
    str claimPayType;

    claimPayType = enum2str(TAMMerchEventPricing::findMerchEvent(_tamMerchandisingEvent.MerchandisingEventID).tfClaimPayType);

    return claimPayType;
}

/// <summary>
/// Shows the correct fields on the discount tab based on the merchandising event category.
/// </summary>
/// <returns>
/// The base return value for active method.
/// </returns>
public int active()
{
    int ret;

    ret = super();
    element.showMerchandisingEventCategoryFields();

    tamMerchandisingEvent.reread();

    // Update grid of merchandising event's items.
    tamPromotionItemME_ds.executeQuery();

    //Lock down bill backs editing if rebates are created;
    if (tamMerchandisingEvent.MerchandisingEventCategory == MCRMerchandisingEventCategory::BillBack
        && tamMerchandisingEvent.createdRebatesExist())
    {
        billBackDefinition.enabled(false);
        billBackRebateAgreementLineGroup.enabled(false);
        tamMerchandisingEvent_ds.allowEdit(false);
        tamMerchandisingEvent_ds.allowDelete(false);
    }
    else
    {
        billBackDefinition.enabled(true);
        billBackRebateAgreementLineGroup.enabled(true);
        if (tamTradePromotion.PromotionStatus == TAMStatus::InProcess)
        {
            tamMerchandisingEvent_ds.allowEdit(true);
            tamMerchandisingEvent_ds.allowDelete(true);
        }
        else
        {
            tamMerchandisingEvent_ds.allowEdit(false);
            tamMerchandisingEvent_ds.allowDelete(false);
        }
    }

    element.enableMerchEventButtons();

    return ret;
}

public void create(boolean _append = false)
{
    NumberSeq               numberSeq;
    TAMCopyTradePromotion   tamCopyTradePromotion;
    MCRMerchandisingEventID merchEventID;

    super(_append);

    ttsbegin;
    // On creation of a merchandising event, assign a number seq for mechandising event id,
    // and default down items and funds from the promotion setup.
    numberSeq = NumberSeq::newGetNum(TAMPromotionParameters::numRefMerchandisingEventID());
    merchEventID = numberSeq.num();
    tamMerchandisingEvent.MerchandisingEventID = merchEventID;
    ttscommit;

    tamCopyTradePromotion = new TAMCopyTradePromotion(tamTradePromotion);
    tamCopyTradePromotion.copyToMerchEvent(merchEventID);

    if (tamMerchandisingEvent.MerchandisingEventCategory == MCRMerchandisingEventCategory::OffInvoice)
    {
        tamMerchandisingEvent.ClaimPayType = MCRClaimPayType::None;
    }

    element.enableByStatus();
}

