public boolean validateDelete()
{
    boolean                     ret;
    TAMMerchandisingEvent       localTAMMerchandisingEvent;

    select count(RecId) from  localTAMMerchandisingEvent group by localTAMMerchandisingEvent.PromotionID
        where localTAMMerchandisingEvent.PromotionID == tamTradePromotion.PromotionID ;

    if (localTAMMerchandisingEvent.RecId >= 1.0)
    {
        ret = checkFailed("@MCR38361");
    }
    else
    {
        ret = super();
    }

    return ret;
}

        public void write()
        {
			DialogButton            dialogButton;
            TAMMerchandisingEvent   childMerchEvents;

            if (TAMTradePromotion.Currency != TAMTradePromotion.orig().Currency)
            {
                // if Merchandising events exist for this TAM agreement
                select firstOnly RecId from childMerchEvents where
                    childMerchEvents.PromotionID == TAMTradePromotion.PromotionID;

                if (childMerchEvents)
                {
                    dialogButton = Box::yesNo(strFmtLB("@SYP4881123"),DialogButton::No);

                    if (dialogButton == DialogButton::No)
                    {
                        return;
                    }
                    else
                    {
                        TAMTradePromotion.transferCurrencyCodeToMerchEvents();
                    }
                }
            }

            super();

            element.numberSeqFormHandlerPromotionId().formMethodDataSourceWrite();
        }

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        ret = element.numberSeqFormHandlerPromotionId().formMethodDataSourceValidateWrite(ret);
    }

    tamTradePromotion.validateOrderDates();

    return ret;
}

public void delete()
{
    TAMPromotionID promotionID;

    promotionID = tamTradePromotion.PromotionID;

    super();

    if (tamTradePromotion.PromotionID != promotionID)
    {
        NumberSeq::releaseNumber(TAMPromotionParameters::numRefPromotionID().NumberSequenceId, promotionID);
    }
}

public int active()
{
    int ret;

    ret = super();

    element.enableStatusButtons();
    element.enableByStatus();

    if (tamTradePromotion.PromotionStatus == TAMStatus::InProcess)
    {
        customers.enabled(true);
        LineViewHeaderTreeGroup.enabled(true);
        LineViewHeaderTPCustBtnGrp.enabled(true);
    }
    else
    {
        customers.enabled(false);
        LineViewHeaderTreeGroup.enabled(false);
        LineViewHeaderTPCustBtnGrp.enabled(false);
    }

    return ret;
}

public void create(boolean _append = false)
{
    //  Clear the form title datasource from the dynalink.
    element.args().record(null);
    //  Assign the promotion Id number sequence.
    element.numberSeqFormHandlerPromotionId().formMethodDataSourceCreatePre();
    super(_append);
    element.numberSeqFormHandlerPromotionId().formMethodDataSourceCreate();

    promotion_PromotionDescription.setFocus();
}

/// <summary>
/// Querys promotions by the template flag based on how
/// the user came in.
/// </summary>
/// <remarks>
/// See also linkActive method of this datasource.
/// </remarks>
public void init()
{
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSourceTAMTradePromotion;
    QueryBuildDataSource    queryBuildDataSourceSalesTable;
    TAMTradePromotion       localTAMTradePromotion;

    element.numberSeqFormHandlerPromotionId();
    super();

    queryBuildDataSourceTAMTradePromotion = this.queryBuildDataSource();

    if (formMode == TAMPromotionMode::Customer)
    {
        queryBuildDataSourceTAMTradePromotion.addRange(fieldNum(TAMTradePromotion, Template)).value(enum2str(NoYes::No));
    }
    else
    {
        queryBuildDataSourceTAMTradePromotion.addRange(fieldNum(TAMTradePromotion, Template)).value(enum2str(NoYes::Yes));
    }

    switch (element.args().dataset())
    {
        // If form opened from deduction record
        case tableNum(TAMDeduction):
            // Add SalesTable datasource to filter customers
            queryBuildDataSourceTAMTradePromotion.clearDynalinks();
            queryBuildDataSourceSalesTable = queryBuildDataSourceTAMTradePromotion.addDataSource(tableNum(SalesTable));
            queryBuildDataSourceSalesTable.addLink(fieldNum(TAMTradePromotion, CustAccount), fieldNum(SalesTable, CustAccount));
            break;
        case tableNum(TAMTradePromotion):
            if (element.args().openMode() == OpenMode::Edit)
            {
                localTAMTradePromotion = element.args().record();
                queryBuildDataSourceTAMTradePromotion.addRange(fieldNum(TAMTradePromotion, PromotionID)).value(localTAMTradePromotion.PromotionID);
                queryBuildDataSourceTAMTradePromotion.clearDynalinks();
            }
            break;
    }

    this.cacheAddMethod(tableMethodStr(TAMTradePromotion, displayCustName));
    this.cacheAddMethod(tableMethodStr(TAMTradePromotion, displayCalcShipQty));
}

public void initValue()
{
    super();

    if (formMode == TAMPromotionMode::Customer)
    {
        tamTradePromotion.Template = false;
    }
    else
    {
        tamTradePromotion.Template = true;
    }
}

public void linkActive()
{
    TAMDeduction                tamDeduction;
    TAMTradePromotion           localTAMTradePromotion;
    TAMMerchandisingEvent       localTAMMerchandisingEvent;
    Query                       query;
    QueryBuildDataSource        queryBuildDataSourceTAMTradePromotion;
    QueryBuildDataSource        queryBuildDataSourceSalesTable;

    switch (element.args().dataset())
    {
        // If parent record/link is deduction.
        case tableNum(TAMDeduction):
            tamDeduction = element.args().record();
            query = this.query();
            queryBuildDataSourceTAMTradePromotion = query.dataSourceTable(tableNum(TAMTradePromotion));
            queryBuildDataSourceSalesTable = query.dataSourceTable(tableNum(SalesTable));

            // Clear ranges that may have been previously set.
            queryBuildDataSourceTAMTradePromotion.clearRange(fieldNum(TAMTradePromotion, PromotionID));
            queryBuildDataSourceSalesTable.clearRange(fieldNum(SalesTable, InvoiceAccount));
            queryBuildDataSourceSalesTable.joinMode(JoinMode::OuterJoin);

            // If promotion ID specified on deduction will show that promotion.
            if (tamDeduction.tamPromotionID)
            {
                queryBuildDataSourceTAMTradePromotion.addRange(fieldNum(TAMTradePromotion, PromotionID))
                    .value(SysQuery::value(tamDeduction.tamPromotionID));
            }
            // Else if merchandising event specified on deduction will show that
            //merchandising event's promotion.
            else if (tamDeduction.mcrMerchandisingEventID)
            {
                select PromotionID from localTAMTradePromotion
                    join TableId from localTAMMerchandisingEvent
                    where localTAMTradePromotion.PromotionID == localTAMMerchandisingEvent.PromotionID
                            && localTAMMerchandisingEvent.MerchandisingEventID == tamDeduction.mcrMerchandisingEventID;

                queryBuildDataSourceTAMTradePromotion.addRange(fieldNum(TAMTradePromotion, PromotionID))
                    .value(SysQuery::value(localTAMTradePromotion.PromotionID));
            }
            // Will show all promotions for deduction's customer.
            else
            {
                queryBuildDataSourceSalesTable.joinMode(JoinMode::ExistsJoin);
                queryBuildDataSourceSalesTable.addRange(fieldNum(SalesTable, InvoiceAccount)).
                    value(SysQuery::value(tamDeduction.AccountNum));
            }
            break;

        case tableNum(TAMTradePromotion):
            // If the form is called with OpenMode::New or OpenMode::Edit, clear the
            // ranges on the TAMTradePromotion table.  Leaving the ranges there will
            // cause the form to refresh or get filtered incorrectly.
            if (element.args().openMode() == OpenMode::New
                || element.args().openMode() == OpenMode::Edit)
            {
                this.query().dataSourceTable(tableNum(TAMTradePromotion)).clearRanges();
            }
            break;
    }

    super();
}

public void modified()
{
    super();
    tamTradePromotion_ds.refreshEx();
}

