public void executeQuery()
{
    Query                   query ;
    QueryBuildDataSource    queryBuildDataSourcePromotionItem;

    query = this.query();

    queryBuildDataSourcePromotionItem = query.dataSourceName(tamPromotionItemME_ds.name());
    queryBuildDataSourcePromotionItem.clearRanges();
    // Show records that have matching merchandising ID.
    queryBuildDataSourcePromotionItem.addRange(fieldNum(TAMPromotionItem, MerchandisingEventID)).value(
        strFmt('((%1 == "%2") && (%1 != %3))',
        fieldStr(TAMPromotionItem, MerchandisingEventID),
        tamMerchandisingEvent.MerchandisingEventID != "" ? SysQuery::value(tamMerchandisingEvent.MerchandisingEventID): "",
        SysQuery::valueEmptyString()));

    super();
}

