public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(TAMFundTable), _formControl);

    Query query = new Query();
    QueryBuildDataSource source;
    QueryBuildRange rangeFundStatus;

    source = query.addDataSource(tableNum(TAMFundTable));
    rangeFundStatus = source.addRange(fieldNum(TAMFundTable, Status));
    rangeFundStatus.value(SysQuery::value(TAMFundStatus::Approved));
    sysTableLookup.addLookupfield(fieldNum(TAMFundTable, FundID ));
    sysTableLookup.parmQuery(query);

    sysTableLookup.performFormLookup();
}

public boolean validate()
{
    TAMPromotionFund    promotionFund;
    boolean             ret;

    ret = super();

    if (tamPromotionFundTP.PromotionID != '')
    {
        select firstonly RecId from promotionFund
            where promotionFund.Fund == tamPromotionFundTP.Fund
                && promotionFund.PromotionID == tamPromotionFundTP.PromotionID;
    }

    if (promotionFund.RecId != 0)
    {
        ret = false;
    }

    return ret;
}

