public void updateCostingLevel()
{
    TAMMerchEventPricing    tamMerchEventPricingLocal;

    update_recordset tamMerchEventPricingLocal
        setting CostingLevel = NoYes::No
        where tamMerchEventPricingLocal.MerchandisingEventID == tamMerchEventPricing.MerchandisingEventID
            && tamMerchEventPricingLocal.CostingLevel == NoYes::Yes
            && tamMerchEventPricingLocal.RecId != tamMerchEventPricing.RecId;
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (tamMerchandisingEvent.MerchandisingEventCategory == MCRMerchandisingEventCategory::LumpSum)
    {
        if (!tamMerchEventPricing.tfPayTo
            || !tamMerchEventPricing.LumpSumAmount)
        {
            throw error(strFmt("@MCR40199", tamMerchEventPricing.MerchandisingEventID));
        }
        else if (!tamMerchEventPricing.lsOffsetLedgerDimension)
        {
            throw error("@MCR4410221");
        }
    }

    return ret;
}

/// <summary>
/// Initializes the pay method from the merchandising event type.
/// </summary>
public void initValue()
{
    TAMPromotionParameters tamPromotionParameters;
    super();

    // if bill back or off invoice set the first record as the costing level.
    if (!TAMMerchEventPricing::findMerchEventCostLevel(tamMerchEventPricing.MerchandisingEventID))
    {
        tamMerchEventPricing.CostingLevel = true;
    }
    else
    {
        tamMerchEventPricing.CostingLevel = false;
    }

    if (tamMerchandisingEvent.MerchandisingEventCategory == MCRMerchandisingEventCategory::LumpSum)
    {
        tamPromotionParameters = TAMPromotionParameters::find();
        tamMerchEventPricing.lsOffsetLedgerDimension = tamPromotionParameters.lsClaimOffsetLedgerDimension;
    }
}

public int active()
{
    int ret;

    ret = super();

    element.disableLumpSumByStatus();

    return ret;
}

public void modified()
{
    super();

    if (tamMerchEventPricing.CostingLevel == NoYes::Yes)
    {
        tamMerchEventPricing_ds.updateCostingLevel();

        tamMerchEventPricing_ds.executeQuery();
    }
}

public void modified()
{
    super();

    tamMerchEventPricing_ds.executeQuery();
}

