public void write()
{
    if (PdsRebateAgreement.PdsRebateUnit != PdsRebateAgreement.orig().PdsRebateUnit
        && pdsRebateAgreement.checkIfLineExists(false))
    {
        warning("@SYP4881125");
    }

    super();
}

public edit PdsRebateType editRebateType(boolean               _set,
                                         PdsRebateAgreement    _pdsRebateAgreement,
                                         PdsRebateType         _pdsRebateType)
{
    PdsRebateAgreement pdsRebateAgreementLocal;

    if (_set)
    {
        if (pdsRebateAgreementLocal.RecId != 0)
        {
            pdsRebateAgreementLocal = PdsRebateAgreement::find(_pdsRebateAgreement.RecId, true);

            ttsbegin;
            pdsRebateAgreementLocal.PdsRebateType = _pdsRebateType;
            pdsRebateAgreementLocal.validTimeStateUpdateMode(ValidTimeStateUpdate::Correction);
            pdsRebateAgreementLocal.modifiedFieldValue(fieldStr(PdsRebateAgreement, PdsRebateType));
            pdsRebateAgreementLocal.doUpdate();
            ttscommit;

            pdsRebateAgreement_ds.executeQuery();
        }
        else
        {
            pdsRebateAgreement.PdsRebateType = _pdsRebateType;
            pdsRebateAgreement.modifiedFieldValue(fieldStr(PdsRebateAgreement, PdsRebateType));

            pdsRebateAgreement_ds.refresh();
        }
    }

    return pdsRebateAgreement.PdsRebateType;
}

public int active()
{
    int     ret;
    boolean cumulateSalesByCustPeriodFlag = pdsRebateAgreement.PdsCumulateSalesBy == PdsCumulationPeriod::CustomizedPeriod;

    ret = super();

    element.enableTAMVendPaymTo();
    element.enableRebateAgreementLineFields();

    pdsRebateAgreement_ds.object(fieldNum(PdsRebateAgreement, RebateCustomizedPeriodType)).enabled(cumulateSalesByCustPeriodFlag);
    pdsRebateAgreement_ds.object(fieldNum(PdsRebateAgreement, RebateCustomizedPeriodType)).skip(!cumulateSalesByCustPeriodFlag);

    return ret;
}

        /// <summary>
        /// Creates the rebate record for bill backs.
        /// </summary>
        public void initValue()
        {
            TAMPromotionParameters  tamPromotionParamaters = TAMPromotionParameters::find();
            PdsRebate               pdsRebate;

            super();

            pdsRebateAgreement.PdsRebateProgramType = PdsRebateProgramType::BillBack;

            pdsRebateAgreement.PdsCustRebateCode = TableGroupAll::All;
            pdsRebateAgreement.PdsCustRebateRelation = '';

            pdsRebateAgreement.PdsItemRebateCode = PdsRebateItemCode::Selection;
            pdsRebateAgreement.PdsItemRebateRelation = '';

            pdsRebateAgreement.ValidFrom = tamTradePromotion.OrdersStartDate;
            pdsRebateAgreement.ValidTo = tamTradePromotion.OrdersEndDate;

            pdsRebateAgreement.PdsUnitType = PdsUnitType::InventoryUnit;
            pdsRebateAgreement.PdsRebateUnit = tamTradePromotion.UnitID;
            pdsRebateAgreement.PdsRebateTakenFrom = PdsRebateTakenFrom::Net;
            pdsRebateAgreement.PdsRebateMinQty = 1;

            pdsRebateAgreement.tamPaymentType = TAMPaymentType::CustomerDeduction;
            pdsRebateAgreement.PdsPaymtType = PdsPaymtType::CustomerDeduction;
            pdsRebateAgreement.RebateGenericCurrency = NoYes::No;
            pdsRebateAgreement.PdsFindNext = NoYes::Yes;
			PdsRebateAgreement.InventDimId = InventDim::findOrCreateBlank().inventDimId;

            if (tamPromotionParamaters.RebateType)
            {
                pdsRebate = PdsRebate::find(tamPromotionParamaters.RebateType);
                pdsRebateAgreement.PdsRebateType = pdsRebate.PdsRebateType;
                pdsRebateAgreement.LedgerDimension = pdsRebate.LedgerDimension;
                pdsRebateAgreement.OffsetLedgerDimension = pdsRebate.OffsetLedgerDimension;
            }
        }

/// <summary>
/// Initializes the <c>RebateID</c> number sequence.
/// </summary>
public void init()
{
    Query   query;

    super();

    query = this.query();
    this.query().dataSourceName(tableStr(PdsRebateAgreement)).addRange(fieldNum(PdsRebateAgreement, tamMerchandisingEventID)).value(SysQuery::valueNotEmptyString());
}

public void modified()
{
    super();

    if (pdsRebateAgreement.PdsCumulateSalesBy != PdsCumulationPeriod::Invoice)
    {
        pdsRebateAgreement.PdsApprovalRequired = NoYes::Yes;
    }
    else
    {
        pdsRebateAgreement.PdsApprovalRequired = NoYes::No;
    }
}

public void modified()
{
    super();

    element.enableRebateAgreementLineFields();
}

public void modified()
{
    super();

    element.enableTAMVendPaymTo();

    element.setRebateAgreementPaymtType();
}

public void write()
{
    if (pdsRebateAgreement.PdsRebateLineBreakType == PdsRebateLineBreakType::Amount)
    {
        pdsRebateAgreementLine.RebateFromQty = 0;
        pdsRebateAgreementLine.RebateToQty = 0;
    }
    else if (pdsRebateAgreement.PdsRebateLineBreakType == PdsRebateLineBreakType::Quantity)
    {
        pdsRebateAgreementLine.RebateFromAmount = 0;
        pdsRebateAgreementLine.RebateToAmount = 0;
    }

    super();
}

public void initValue()
{
    super();

    pdsRebateAgreementLine.Currency = pdsRebateAgreement.RebateCurrency;
    pdsRebateAgreementLine.RebateUnit = pdsRebateAgreement.PdsRebateUnit;
}

void clicked()
{
    super();
    ttsbegin;
    tamTradePromotion.PromotionStatus   = TAMStatus::InProcess;
    tamTradePromotion.wfApprovalStatus  = TAMPromoMgmtApprovalStatus::NotSubmitted;
    tamTradePromotion.update();
    ttscommit;

    tamTradePromotion_ds.reread();
    tamTradePromotion_ds.refresh();

    element.enableStatusButtons();
    element.enableByStatus();
    element.setRebateAgreementStatus();
}

