    tamTradePromotion.wfApprovalStatus  = TAMPromoMgmtApprovalStatus::Approved;
    tamTradePromotion.update();
    ttscommit;

    tamTradePromotion_ds.reread();
    tamTradePromotion_ds.refresh();

    element.enableStatusButtons();
    element.enableByStatus();
    element.setRebateAgreementStatus();
}

