/// <summary>
/// Copies a trade promotion or trade promotion template.
/// </summary>
void clicked()
{
    Args            args;
    FormRun         formRun;

    element.updateMerchandiseCost();

    args = new Args();
    args.record(tamTradePromotion);
    formRun = new MenuFunction(menuitemDisplayStr(TAMCopyTradePromotions), MenuItemType::Display).create(args);

    if (formRun)
    {
        formRun.run();
        formRun.wait();
    }

    if (formRun.closedOk())
    {
        if (TAMTradePromotion.Template)
        {
            info("@MCR38254");
        }
        else
        {
            info("@MCR26160");
        }
    }

    super();

    tamTradePromotion_ds.research(true);
}

/// <summary>
/// Creates all the specific customer promotions from a template.
/// </summary>
void clicked()
{
    TAMCopyTradePromotion   tamCopyTradePromotion;
    NumberSeq               numberSeq;
    TAMPromotionID          promotionID;

    super();
    element.updateMerchandiseCost();

    numberSeq = NumberSeq::newGetNum(TAMPromotionParameters::numRefPromotionID());
    tamCopyTradePromotion = new TAMCopyTradePromotion(tamTradePromotion);
    ttsbegin;
    promotionID = numberSeq.num();
    ttscommit;

    tamCopyTradePromotion.copyToNewPromotion(promotionID, tamTradePromotion.PromotionDescription, true, true, true, true, true, true, true, false);

    info("@MCR26160");
    tamTradePromotion_ds.research();
}

void clicked()
{
    super();
    if (!tamTradePromotion.OverriddenShipQty)
    {
        warning("@MCR28485");
    }
}

public void pageActivated()
{
    super();
    tamTradePromotion_ds.reread();
}

