{
    Common ret;

    ret = hcmWorkerLookupCurrentCompany.lookupWorker(this);

    return ret;
}

public Common resolveReference()
{
    HcmWorker hcmWorker;

    hcmWorker = super();

    if (hcmWorker != null && !hcmWorkerLookupCurrentCompany.validateWorker(hcmWorker.RecId))
    {
        hcmWorker = null;
    }

    return hcmWorker;
}

