    {
        mcrCustTreeDatasourceTradePromo.updateTree(_idx);
    }
}

boolean expanding(int _idx, FormTreeExpand _action, anytype _data)
{
    boolean ret;

    ret = super(_idx, _action, _data);

    if (mcrCustTreeDatasourceTradePromo)
