        mcrCustTreeDatasourceTradePromo.expanding(_idx, _action, _data);
    }

    return ret;
}

void selectionChanged(FormTreeItem oldItem, FormTreeItem newItem, FormTreeSelect how)
{
    int root = this.getRoot();

    super(oldItem, newItem, how);

