    {
        mcrCustTreeDatasourceTradePromo.selectionChanged(oldItem, newItem);
    }
}

public boolean selectionChanging(FormTreeItem _oldItem, FormTreeItem _newItem, FormTreeSelect _how)
{
    boolean ret;

    ret = super(_oldItem, _newItem, _how);

    if (!ret && mcrCustTreeDatasourceTradePromo)
    {
        ret = mcrCustTreeDatasourceTradePromo.selectionChanging(_oldItem, _newItem, _how);
    }
    return ret;
}

/// <summary>
/// Loads the promotion item table with the items
/// under the selected node of the item hierarchy.
/// </summary>
void clicked()
{
    TAMPromotionCust localTAMPromotionCust;
    MCRCustCategory  localMCRCustCategory;
    int idx;

    idx = lineViewHeaderCtrlCustTree.getFirstSelected();

    while (idx)
    {
        localMCRCustCategory = MCRCustCategory::find(lineViewHeaderCtrlCustTree.getItem(idx).data());
        if (localMCRCustCategory.Customer)
        {
            localTAMPromotionCust.PromotionID = tamTradePromotion.PromotionID;
            localTAMPromotionCust.CustAccount = CustTable::findRecId(localMCRCustCategory.Customer).AccountNum;
            if (!TAMPromotionCust::exist(localTAMPromotionCust.PromotionID, localTAMPromotionCust.CustAccount))
            {
                localTAMPromotionCust.insert();
            }
        }

        // Must expand the node before the tree knows this node has children
        lineViewHeaderCtrlCustTree.expand(idx, FormTreeExpand::Expand);
        TAMPromotionCust::copyChildren(lineViewHeaderCtrlCustTree, lineViewHeaderCtrlCustTree.getChild(idx), tamTradePromotion.PromotionID);
        lineViewHeaderCtrlCustTree.expand(idx, FormTreeExpand::Collapse);

        idx = lineViewHeaderCtrlCustTree.getNextSelected(idx);
    }

    tamPromotionCust_ds.executeQuery();
}

/// <summary>
/// Removes the selected customers from the current trade promotion.
/// </summary>
void clicked()
{
    TAMPromotionCust localTAMPromotionCust;

    for (localTAMPromotionCust = tamPromotionCust_ds.getFirst(true) ? tamPromotionCust_ds.getFirst(true): tamPromotionCust_ds.cursor();  localTAMPromotionCust;
    localTAMPromotionCust = tamPromotionCust_ds.getNext())
    {
        localTAMPromotionCust.delete();
    }
    tamPromotionCust_ds.executeQuery();
}

