{
    super();
    //Set focus on grid to refresh in case a value was modified.
    MEFundsGrid.setFocus();
}

public void pageActivated()
{
    super();

    if (tamMerchandisingEvent.MerchandisingEventCategory == MCRMerchandisingEventCategory::BillBack
        && !pdsRebateAgreement.RecId)
    {
        pdsRebateAgreement_ds.create();

        // Check if all fields that are optional parameter driven are set and write record.
        // Required if initValue covers initial setting of all fields, and an agreement line is created.
        if (pdsRebateAgreement.PdsRebateUnit
            && pdsRebateAgreement.PdsRebateProgramType
            && pdsRebateAgreement.validateWrite())
        {
            pdsRebateAgreement_ds.write();
        }
    }
    //Set focus on grid to refresh in case a value was modified.
    Discount.setFocus();
}

/// <summary>
/// Approves the lump sum for claim processing.
/// </summary>
void clicked()
{
    if (Box::yesNo("@MCR26488", DialogButton::Yes))
    {
        if (tamMerchEventPricing.LumpSumApprovedAmount  != 0
            && tamMerchEventPricing.checkLumpSumPaidAmt(tamMerchEventPricing)
            && tamTradePromotion.checkPerformanceDates(tamTradePromotion))
        {
            TAMLumpSumPaymentPost::main(tamMerchEventPricing);
        }

        tamMerchEventPricing_ds.reread();
        element.disableLumpSumByStatus();
        element.enableStatusButtons();
        tamTradePromotion_ds.refreshEx();
    }
    super();
}

/// <summary>
/// Approves the lump sum for claim processing.
/// </summary>
void clicked()
{
    if (Box::yesNo("@MCR26477", DialogButton::Yes))
    {
        tamMerchEventPricing.LumpSumStatus = MCRLumpSumStatus::Decline;

        element.disableLumpSumByStatus();
        tamMerchEventPricing_ds.refresh();
    }
    super();
}

