{
    boolean ret;

    ret = super();

    pdsRebateAgreement_ds.active();

    return ret;
}

public void jumpRef()
{
    Args args = new Args();
    MenuFunction menuFunction;

    args.caller(element);
    menuFunction = new MenuFunction(menuitemDisplayStr(PdsRebateType),
    MenuItemType::Display);
    menuFunction.run(args);
}

public void lookup()
{
    PdsRebate::tamRebateProgramTypeLookup(this, PdsRebateProgramType::BillBack);
}

public boolean validate()
{
    boolean ret;

    TAMMerchEventPricing localTAMMerchEventPricing;

    localTAMMerchEventPricing = TAMMerchEventPricing::findMerchEventCostLevel(localTAMMerchEventPricing.MerchandisingEventID);

    ret = super();

    if (this.value() == true
        && localTAMMerchEventPricing
        && localTAMMerchEventPricing.RecId != tamMerchEventPricing.RecId)
    {
        tamMerchEventPricing.CostingLevel = false;
        error("@MCR26580");
        ret = false;
    }

    return ret;
}

