void clicked()
{
    TAMPromotionCust localTAMPromotionCust;
    MCRCustCategory  localMCRCustCategory;
    int idx;

    idx = ctrlCustTree.getFirstSelected();

    while (idx)
    {
        localMCRCustCategory = MCRCustCategory::find(ctrlCustTree.getItem(idx).data());
