        TAMPromotionCust::copyChildren(ctrlCustTree, ctrlCustTree.getChild(idx), tamTradePromotion.PromotionID);
        ctrlCustTree.expand(idx, FormTreeExpand::Collapse);

        idx = ctrlCustTree.getNextSelected(idx);
