public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    List exportOptions = _menuOptions.customMenuItems();

    var exportOption = OfficeGeneratedExportMenuItem::construct(tableStr(TAMVendorRebateAgreementHeaderEntity), ExportAgreementHeaderAndLinesExcelOptionId);
    exportOption.displayName(strfmt("@SYS76681", "@SCM:TAMVendorRebateAgreementHeaderAndLines", '', curExt()));
    exportOptions.addEnd(exportOption);
}

public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = null;

    switch (_menuItem.id())
    {
        case ExportAgreementHeaderAndLinesExcelOptionId:
            context = ExportToExcelDataEntityContext::constructDefault(tableStr(TAMVendorRebateAgreementHeaderEntity));

            var headerEntityInfo = context.findEntityByName(tableStr(TAMVendorRebateAgreementHeaderEntity));

            var headerFilterBuilder = new ExportToExcelFilterTreeBuilder(tablestr(TAMVendorRebateAgreementHeaderEntity));
            var headerRebateAgreementIdFilter = headerFilterBuilder.areEqual(fieldStr(TAMVendorRebateAgreementHeaderEntity, RebateAgreementId), int642Str(TAMVendRebateAgreement.RecId));
            headerEntityInfo.filter(headerFilterBuilder.and(headerFilterBuilder.companyFilter(), headerRebateAgreementIdFilter));

            var productSelectionEntityInfo = context.addEntityDefault(tableStr(TAMVendorRebateAgreementProductSelectionEntity));

            var productSelectionFilterBuilder = new ExportToExcelFilterTreeBuilder(tablestr(TAMVendorRebateAgreementProductSelectionEntity));
            var productSelectionRebateAgreementIdFilter = productSelectionFilterBuilder.areEqual(fieldStr(TAMVendorRebateAgreementProductSelectionEntity, RebateAgreementId), int642Str(TAMVendRebateAgreement.RecId));
            productSelectionEntityInfo.filter(productSelectionFilterBuilder.and(productSelectionFilterBuilder.companyFilter(), productSelectionRebateAgreementIdFilter));

            var lineEntityInfo = context.addEntityDefault(tableStr(TAMVendorRebateAgreementLineEntity));

            var lineFilterBuilder = new ExportToExcelFilterTreeBuilder(tablestr(TAMVendorRebateAgreementLineEntity));
            var lineRebateAgreementIdFilter = lineFilterBuilder.areEqual(fieldStr(TAMVendorRebateAgreementLineEntity, RebateAgreementId), int642Str(TAMVendRebateAgreement.RecId));
            lineEntityInfo.filter(lineFilterBuilder.and(lineFilterBuilder.companyFilter(), lineRebateAgreementIdFilter));
            break;
    }

    return context;
}

public boolean canActivateApprovalWorkflow(TableId tableId, RecId recId)
{
    WorkflowVersionTable versionTable = WorkflowTable::findActiveTemplate(workFlowTypeStr(TAMVendRebateAppr)).getActiveVersion();
    SysDictWorkflowType     sysDictWorkflowTemplate;
    sysDictWorkflowTemplate = new SysDictWorkflowType(workFlowTypeStr(TAMVendRebateAppr));

    if (versionTable.ActivationConditionType == NoYes::Yes
        && sysDictWorkflowTemplate.disableActivationConditions() == false)
    {
        return Workflow::evaluateWorkflowActivationCondition(versionTable, recId, tableId);
    }

    //if there is not activation condition set up, allow activation by default
    return true;
}

public boolean canSubmitToWorkflow()
{
    boolean ret;

    ret = super();

    if (ret && tamVendRebateAgreement.RecId)
    {
        ret = element.canActivateApprovalWorkflow(tableNum(TAMVendRebateAgreement), tamVendRebateAgreement.RecId);
    }

    return ret;
}

