protected boolean mustEnableItemRebateRelation()
{
    return tamVendRebateAgreement.ItemRebateCode != TAMVendRebateItemCode::All &&
           tamVendRebateAgreement.ItemRebateCode != TAMVendRebateItemCode::Selection;
}

/// <summary>
/// Determines if the <c>VendRebateRelation</c> is enabled.
/// </summary>
/// <returns>true if enabled; otherwise, false.</returns>
protected boolean mustEnableVendRebateRelation()
{
    return tamVendRebateAgreement.VendRebateCode != TableGroupAll::All;
}

private void enableField(FieldId _fieldId, boolean _enabled)
{
    if (!_enabled)
    {
        tamVendRebateAgreement.(_fieldId) = '';
    }

    tamVendRebateAgreement_ds.object(_fieldId).enabled(_enabled);
    tamVendRebateAgreement_ds.object(_fieldId).skip(!_enabled);
}

public void initRecord()
{
    if (tamVendRebateAgreement.RecId
        && tamVendRebateAgreement.checkRelatedRebateTransExist())
    {
        tamVendRebateAgreement_ds.allowEdit(false);
        buttonGroupItems.enabled(false);
    }
    else
    {
        tamVendRebateAgreement_ds.allowEdit(true);
        buttonGroupItems.enabled(true);

        this.enableField(fieldNum(TAMVendRebateAgreement, VendRebateRelation), this.mustEnableVendRebateRelation());
        this.enableField(fieldNum(TAMVendRebateAgreement, ItemRebateRelation), this.mustEnableItemRebateRelation());

        boolean cumulatePurchaseByVendPeriodFlag = tamVendRebateAgreement.CumulatePurchaseBy == TAMCumulationPeriod::CustomizedPeriod;
        tamVendRebateAgreement_ds.object(fieldNum(TAMVendRebateAgreement, RebateCustomizedPeriodType)).enabled(cumulatePurchaseByVendPeriodFlag);
        tamVendRebateAgreement_ds.object(fieldNum(TAMVendRebateAgreement, RebateCustomizedPeriodType)).skip(!cumulatePurchaseByVendPeriodFlag);
    }

    element.updateDesign(InventDimFormDesignUpdate::Active);
    element.setAvailFields();
}

