}

public void setAvailFields()
{
    boolean workflowInReview;
    boolean enableValidation;
    WorkflowTrackingStatusTable workflowTrackingStatus;

    boolean showDimensionSelection = this.showDimensionSelection();
    SelectionProductDimensions.visible(showDimensionSelection);
    SelectionTrackingDimensions.visible(showDimensionSelection);
    SelectionStorageDimensions.visible(showDimensionSelection);
    ProductDimensions.visible(!showDimensionSelection);
    TrackingDimensions.visible(!showDimensionSelection);
    StorageDimensions.visible(!showDimensionSelection);

    vendRebateAgreementItemList.enabled(showDimensionSelection);
    vendRebateAgreementItemList.visible(showDimensionSelection);

    if (!WorkflowTable::findActiveTemplate(workFlowTypeStr(TAMVendRebateAppr)).RecId != 0)
    {
        workflowEnabled = false;
        enableValidation = true;
    }
    else
    {
        if (element.canSubmitToWorkflow())
        {
            workflowEnabled = true;
            enableValidation = false;
        }
        else
        {
            workflowTrackingStatus = Workflow::findTrackingStatusForDocument(tamVendRebateAgreement);
            if (workflowTrackingStatus)
            {
                // Record has a tracking status and been approved yet.
                if (workflowTrackingStatus.TrackingStatus == WorkflowTrackingStatus::Completed)
                {
                    workflowEnabled = true;
                    enableValidation = true;
                }
                else
                {
                    // Record has a tracking status but has not been approved yet.
                    workflowEnabled = true;
                    enableValidation = false;
                }
            }
            else
            {
                // Record does not have a tracking status.
                workflowEnabled = true;
                enableValidation = true;
            }
        }
    }

    // can only approve an agreement with a line
    tamVendRebateAgreementValidate.enabled(tamVendRebateAgreementLine.RecId != 0
                                            && enableValidation);

    // If workflow is in the submitted status, then lock down the record
    // so it can't be edited.
    if (workflowEnabled)
    {
        workflowInReview = tamVendRebateAgreement.wfApprovalStatus == TAMVendRebateApprovalStatus::Submitted
            || tamVendRebateAgreement.checkRelatedRebateTransExist();

        tamVendRebateAgreement_ds.allowEdit(!workflowInReview);
        tamVendRebateAgreementLine_ds.allowEdit(!workflowInReview);
        tamVendRebateAgreement_ds.allowDelete(!workflowInReview);
        tamVendRebateAgreementLine_ds.allowCreate(!workflowInReview);
        tamVendRebateAgreementLine_ds.allowDelete(!workflowInReview);
    }
}

public void updateDesign(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (!inventDimFormSetUp)
            {
                inventDimFormSetUp = InventDimCtrl_Frm_PdsRebateAgreement::newFromForm(element);
                inventDimFormSetUp.defaultInventDimParm();
                inventDimFormSetUp.inventDimEnable();
                inventDimFormSetUp.formSetControls(true);
            }
            break;

        case InventDimFormDesignUpdate::Active:
            inventDimFormSetUp.formActiveSetup();

            InventDimParm inventDimParmEnabled;
            ItemId itemId = this.itemId();

            if (itemId)
            {
                InventTable inventTable = InventTable::find(ItemId);

                InventDimGroupSetup inventDimGroupSetup = InventDimGroupSetup::newDimensionGroups(inventTable.productDimensionGroup(),
                                                                              inventTable.storageDimensionGroup(),
                                                                              inventTable.trackingDimensionGroup());

                inventDimGroupSetup.inventDimParmActive(inventDimParmEnabled);
            }

            inventDimFormSetUp.parmDimParmEnabled(inventDimParmEnabled);
            inventDimFormSetUp.formSetControls(true);
            break;
    }
}

