public void executeQuery()
{
    if (vendorRebateAgreementId)
    {
        this.query().dataSourceTable(tableNum(TAMVendRebateAgreement)).addRange(fieldnum(TAMVendRebateAgreement, Recid)).value(vendorRebateAgreementId);
    }

    super();
}

public void write()
{
    DialogButton            dialogButton;

    if ((tamVendRebateAgreement.RebateCurrency != tamVendRebateAgreement.orig().RebateCurrency
        || tamVendRebateAgreement.RebateUnit != tamVendRebateAgreement.orig().RebateUnit)
        && tamVendRebateAgreement.checkIfLineExists(false))
    {
        if (tamVendRebateAgreement.RebateCurrency != tamVendRebateAgreement.orig().RebateCurrency)
        {
            dialogButton = Box::yesNoCancel(strFmtLB("@SYP4881124"), DialogButton::Yes);
            switch (dialogButton)
            {
                case DialogButton::Yes:
                    tamVendRebateAgreement.convertCurrencyCode();
                    break;
                case DialogButton::No: //do nothing. currency will be updated in the table method
                    break;
                case DialogButton::Cancel:
                    tamVendRebateAgreement.RebateCurrency = tamVendRebateAgreement.orig().RebateCurrency; //restore original currency
                    tamVendRebateAgreement_ds.refresh();
                    return;
            }
        }

        if (tamVendRebateAgreement.RebateUnit != tamVendRebateAgreement.orig().RebateUnit)
        {
            warning("@SYP4881125");
        }
    }

    inventDim_ds.write();

    tamVendRebateAgreement.InventdimId = InventDim::findOrCreate(inventDim).InventDimId;

    super();

    element.updateWorkflowControls();
    element.setAvailFields();
}

