public void research(boolean _retainPosition = false)
{
    if (_retainPosition)
    {
        tamVendRebateAgreement_ds.reread();
        tamVendRebateAgreement_ds.refresh();
        tamVendRebateAgreement_ds.rereadReferenceDataSources();
    }
    else
    {
        super();
    }
}

