protected void updateDependencies()
{
    if (tamVendRebateAgreement.ItemRebateCode == TAMVendRebateItemCode::Table)
    {
        tamVendRebateAgreement.RebateUnit =
            InventTableModule::find(
                tamVendRebateAgreement.ItemRebateRelation,
                ModuleInventPurchSales::Purch).UnitId;

        inventDim.data(InventDim::findOrCreateBlank());
        tamVendRebateAgreement.InventDimId = inventDim.inventDimId;
    }
}

