Method.Source:
    [Form]
    public class TAMWhatIfPromotionAnalysis extends FormRun
    {
        // bar 1
        real barPlanBQ;
        // bar 2
        real barPlanShip;
        // bar 3
        real barActualShip;

        // bar 1
        real barPlanBaseSales;
        // bar 2
        real barPlannedPromoSales;
        // bar 3
        real barActualPromoSales;

}

void drawQtyGraph()
{
    #MACROLIB.ChartFx

    Graphics barGraph = Graphics::newGraphicsNoTitlesSysChart(QtyChart, QtyGraphicsTmpData);

    // Bar graph type and show 3D to name.
    barGraph.parmCreateType(#CT_LEGEND | #CT_3D);

    barGraph.parmCreateExtStyle(#CSE_LANDSCAPE);

    barGraph.create();
    barGraph.resetGraph();

    barGraph.parmTitle("@MCR28185");
    barGraph.parmTitleYAxis("@SYS59970");

    // x z y  axis
    barGraph.loadData("@SCM:Base", "@SYS32845", barPlanBQ);
    barGraph.loadData("@SCM:PromotionalPlanned", "@SYS32845", barPlanShip);
    barGraph.loadData("@SCM:PromotionalActual", "@SYS33313", barActualShip);

    barGraph.showGraph();
}

void drawSalesGraph()
{
    #MACROLIB.ChartFx

    Graphics barGraph = Graphics::newGraphicsNoTitlesSysChart(SalesChart, SalesGraphicsTmpData);

    // Bar graph type and show 3D to name.
    barGraph.parmCreateType(#CT_LEGEND | #CT_3D);

    barGraph.parmCreateExtStyle(#CSE_LANDSCAPE);

    barGraph.create();
    barGraph.resetGraph();

    barGraph.parmTitle("@MCR28218");
    barGraph.parmTitleYAxis("@SYS53072");

    // x z y  axis
    barGraph.loadData("@SCM:Base", "@SYS32845", barPlanBaseSales);
    barGraph.loadData("@SCM:PromotionalPlanned", "@SYS32845", barPlannedPromoSales);
    barGraph.loadData("@SCM:PromotionalActual", "@SYS33313", barActualPromoSales);

    barGraph.showGraph();
}

public void run()
{
    super();
    this.setValues();
    this.drawQtyGraph();
    this.drawSalesGraph();
}

void setValues()
{
    TAMWhatIfAnalysis tamWhatIfAnalysis;

    tamWhatIfAnalysis = new TAMWhatIfAnalysis(TAMTradePromotion);
    tamWhatIfAnalysis.calcAllValues();

    IncrementalQuantity.realValue(tamWhatIfAnalysis.getIncrementalQty());
    LumpSumCost.realValue(tamWhatIfAnalysis.getLSCost());
    SalesLumpSumPlanned.realValue(tamWhatIfAnalysis.getLSCost());
    SalesLumpSumActual.realValue(tamWhatIfAnalysis.getLSCost());
    AverageUnitPrice.realValue(tamWhatIfAnalysis.getAvgUnitPrice());
    OIUnitDisc.realValue(tamWhatIfAnalysis.getUnitOIDisc());
    BBUnitDisc.realValue(tamWhatIfAnalysis.getUnitBBDisc());
    TotalUnitDisc.realValue(tamWhatIfAnalysis.getTotalVarUnitDisc());
    TotalVariableUnitDisc.realValue(tamWhatIfAnalysis.getTotalVarUnitDisc());
    NetUnitDiscPrice.realValue(tamWhatIfAnalysis.getUnitNetDiscPrice());
    ExtendedPrice.realValue(tamWhatIfAnalysis.getExtAvgPrice());
    OIExtDisc.realValue(tamWhatIfAnalysis.getExtOIDisc());
    BBExtDisc.realValue(tamWhatIfAnalysis.getExtBBDisc());
    TotalExtDisc.realValue(tamWhatIfAnalysis.getTotalExtDisc());
    NetDiscExtPrice.realValue(tamWhatIfAnalysis.getExtNetDiscPrice());
    TotalVariableCosts.realValue(tamWhatIfAnalysis.getTotalVarCost());
    TotalCosts.realValue(tamWhatIfAnalysis.getTotalCost());
    Sales.realValue(tamWhatIfAnalysis.getSales());
    PromotionalSales.realValue(tamWhatIfAnalysis.getNetPromoSales());
    NetIncrementalSales.realValue(tamWhatIfAnalysis.getNetIncSales());

    TAMActualQuantities tamActualQuantities = new TAMActualQuantities(TAMTradePromotion);
    tamActualQuantities.calcAllQuantities();
    //actualBaseQty.realValue(tamActualQuantities.getActualBaseQty());
    actualShipQty.realValue(tamActualQuantities.getInvoicedQty());
    actualLiftPercent.realValue(tamActualQuantities.getLiftPercent());
    actualIncrease.realValue(tamActualQuantities.getInvoicedQty() - TAMTradePromotion.BaseUnits);

    AfterPromotionSales.realValue(tamActualQuantities.getInvoicedQty() * tamWhatIfAnalysis.getAvgUnitPrice());
    AfterPromotionCost.realValue(tamActualQuantities.getInvoicedQty() * tamWhatIfAnalysis.getTotalVarUnitDisc());
    AfterPromotionSalesDiscounted.realValue(tamActualQuantities.getInvoicedQty() * tamWhatIfAnalysis.getUnitNetDiscPrice());

    TAMActualContribution tamActualContribution = new TAMActualContribution(TAMTradePromotion);
    tamActualContribution.calcAllActualContributions();
    ActualContribution.realValue(tamActualContribution.getActualContribution());
    ActualIncContribution.realValue(tamActualContribution.getActualIncContribution());

    //Quantity chart values
    barPlanBQ = TAMTradePromotion.BaseUnits;
    barPlanShip = TAMTradePromotion.OverriddenShipQty;
    barActualShip = tamActualQuantities.getInvoicedQty();

    //Sales chart values
    barPlanBaseSales = tamWhatIfAnalysis.getSales();
    barPlannedPromoSales = tamWhatIfAnalysis.getNetPromoSales();
    barActualPromoSales = (tamWhatIfAnalysis.getUnitNetDiscPrice() * tamActualQuantities.getInvoicedQty()) - tamWhatIfAnalysis.getLSCost();

    // only allow access to actual values and graphs if the promotion
    // status is customer approved or closed.
    if (TAMTradePromotion.PromotionStatus == TAMStatus::CustomerApproved || TAMTradePromotion.PromotionStatus == TAMStatus::Closed)
    {
        TAMActualCost.enabled(true);
    }
    else
    {
        TAMActualCost.enabled(false);
    }
}
