Method.Source:
[Form]
public class TMSAccessorialAssignment extends FormRun
{
    TMSCarrierCode      carrierId;
    QueryBuildRange     qbr_Carrier;
}

/// <summary>
    /// Enables fields.
    /// </summary>
    /// <param name="_assignmentLevel">
    /// Determines the selected target of accessorial assignment.
    /// </param>
    public void enableFields(TMSAccessorialAssignmentTarget _assignmentLevel)
    {
        boolean carrierOrNoneSelected = _assignmentLevel == TMSAccessorialAssignmentTarget::Carrier || _assignmentLevel == TMSAccessorialAssignmentTarget::None;
        boolean hubOrNoneSelected = _assignmentLevel == TMSAccessorialAssignmentTarget::Hub || _assignmentLevel == TMSAccessorialAssignmentTarget::None;
        boolean addressFilterEnabled = !(tmsAccessorialAssignment.AlwaysApply || _assignmentLevel == TMSAccessorialAssignmentTarget::Hub);

        if (carrierId)
        {
            tmsAccessorialAssignment_ds.object(fieldNum(TMSAccessorialAssignment,CarrierCode)).allowEdit(false);
        }

        tmsAccessorialAssignment_HubAccessorialId.enabled(hubOrNoneSelected);
        tmsAccessorialAssignment_CarrierAccessorialId.enabled(carrierOrNoneSelected);

        tmsAccessorialAssignment_CarrierCode.enabled(carrierOrNoneSelected);
        tmsAccessorialAssignment_HubMasterCode.enabled(hubOrNoneSelected);
        tmsAccessorialAssignment_EffectiveUTCDateTimeFrom.enabled(!tmsAccessorialAssignment.AlwaysApply);
        tmsAccessorialAssignment_EffectiveUTCDateTimeTo.enabled(!tmsAccessorialAssignment.AlwaysApply);
        tmsAccessorialAssignment_WeightLow.enabled(!tmsAccessorialAssignment.AlwaysApply);
        tmsAccessorialAssignment_WeightHigh.enabled(!tmsAccessorialAssignment.AlwaysApply);
        tmsAccessorialAssignment_CustomerCode.enabled(!tmsAccessorialAssignment.AlwaysApply);
        tmsAccessorialAssignment_CustomerInvoiceCode.enabled(!tmsAccessorialAssignment.AlwaysApply);
        tmsAccessorialAssignment_State.enabled(addressFilterEnabled);
        tmsAccessorialAssignment_CountryRegion.enabled(addressFilterEnabled);
        tmsAccessorialAssignment_PostalCodeLow.enabled(addressFilterEnabled);
        tmsAccessorialAssignment_PostalCodeHigh.enabled(addressFilterEnabled);
        tmsAccessorialAssignment_ZoneMasterCode.enabled(addressFilterEnabled);
        tmsAccessorialAssignment_Zone.enabled(addressFilterEnabled);
        tmsAccessorialAssignment_FreightTermCode.enabled(!tmsAccessorialAssignment.AlwaysApply);
        tmsAccessorialAssignment_EquipmentCode.enabled(!tmsAccessorialAssignment.AlwaysApply);
        tmsAccessorialAssignment_ValueLow.enabled(!tmsAccessorialAssignment.AlwaysApply);
        tmsAccessorialAssignment_ValueHigh.enabled(!tmsAccessorialAssignment.AlwaysApply);
        tmsAccessorialAssignment_Residential.enabled(!tmsAccessorialAssignment.AlwaysApply);
        tmsAccessorialAssignment_InventSiteId.enabled(!tmsAccessorialAssignment.AlwaysApply);
        tmsAccessorialAssignment_InventLocationId.enabled(!tmsAccessorialAssignment.AlwaysApply);
    }

public void init()
{
    TMSCarrier      carrier;

    if (element.args().record() && tableNum(TMSCarrier) == element.args().record().TableId)
    {
        carrier = element.args().record();
        carrierId = carrier.CarrierCode;
    }

    super();
}

// <remarks>
// Add a post-method handler for setting the correct visibility values when extending the <c>TMSFeeType</c> enumeration.
// </remarks>
public void selectFeeType(TMSFeeType _feeType)
{
    switch (_feeType)
    {
        case TMSFeeType::Flat:
            tmsAccessorialAssignment_UOM.visible(false);
            tmsAccessorialAssignment_UOMDivisor.visible(false);
            tmsAccessorialAssignment_MaxCharge.visible(false);
            tmsAccessorialAssignment_MinCharge.visible(false);
            tmsAccessorialAssignment_Fee.visible(true);
            tmsAccessorialAssignment_Percent.visible(false);
            tmsAccessorialAssignment_RegionCode.visible(false);
            tmsAccessorialAssignment_Offset.visible(false);
            tmsAccessorialAssignment_MileageEngineCode.visible(false);
            break;

        case TMSFeeType::FuelSurcharge:
            tmsAccessorialAssignment_UOM.visible(true);
            tmsAccessorialAssignment_UOMDivisor.visible(true);
            tmsAccessorialAssignment_MaxCharge.visible(true);
            tmsAccessorialAssignment_MinCharge.visible(true);
            tmsAccessorialAssignment_Fee.visible(false);
            tmsAccessorialAssignment_Percent.visible(false);
            tmsAccessorialAssignment_RegionCode.visible(true);
            tmsAccessorialAssignment_Offset.visible(true);

            if (tmsAccessorialAssignment_UOM.valueStr() == enum2str(TMSUOM::Mileage))
            {
                tmsAccessorialAssignment_MileageEngineCode.visible(true);
            }
            else
            {
                tmsAccessorialAssignment_MileageEngineCode.visible(false);
            }

            break;

        case TMSFeeType::Percent:
            tmsAccessorialAssignment_UOM.visible(true);
            tmsAccessorialAssignment_UOMDivisor.visible(true);
            tmsAccessorialAssignment_MaxCharge.visible(true);
            tmsAccessorialAssignment_MinCharge.visible(true);
            tmsAccessorialAssignment_Fee.visible(false);
            tmsAccessorialAssignment_Percent.visible(true);
            tmsAccessorialAssignment_RegionCode.visible(false);
            tmsAccessorialAssignment_Offset.visible(false);

            if (tmsAccessorialAssignment_UOM.valueStr() == enum2str(TMSUOM::Mileage))
            {
                tmsAccessorialAssignment_MileageEngineCode.visible(true);
            }
            else
            {
                tmsAccessorialAssignment_MileageEngineCode.visible(false);
            }

            break;

        case TMSFeeType::PerUOM:
            tmsAccessorialAssignment_UOM.visible(true);
            tmsAccessorialAssignment_UOMDivisor.visible(true);
            tmsAccessorialAssignment_MaxCharge.visible(true);
            tmsAccessorialAssignment_MinCharge.visible(true);
            tmsAccessorialAssignment_Fee.visible(true);
            tmsAccessorialAssignment_Percent.visible(false);
            tmsAccessorialAssignment_RegionCode.visible(false);
            tmsAccessorialAssignment_Offset.visible(false);

            if (tmsAccessorialAssignment_UOM.valueStr() == enum2str(TMSUOM::Mileage))
            {
                tmsAccessorialAssignment_MileageEngineCode.visible(true);
            }
            else
            {
                tmsAccessorialAssignment_MileageEngineCode.visible(false);
            }

            break;

    }
}

public void initValue()
{
    TMSCarrierAccessorial   carrierAccessorial;
    TMSHubAccessorial       hubAccessorial;

    super();

    if (tmsAccessorialAssignment.CarrierAccessorialId!= '')
    {
        carrierAccessorial = TMSCarrierAccessorial::find(tmsAccessorialAssignment.CarrierAccessorialId);
        tmsAccessorialAssignment.CarrierCode             = carrierAccessorial.CarrierCode;
        tmsAccessorialAssignment.CarrierServiceCode      = carrierAccessorial.CarrierServiceCode;
        tmsAccessorialAssignment.AccessorialMasterCode   = carrierAccessorial.AccessorialMasterCode;
        tmsAccessorialAssignment.BillingGroupId          = carrierAccessorial.BillingGroupId;
        tmsAccessorialAssignment.ExternalCode            = carrierAccessorial.ExternalCode;
    }

    if (tmsAccessorialAssignment.HubAccessorialId!= '')
    {
        hubAccessorial = TMSHubAccessorial::find(tmsAccessorialAssignment.HubAccessorialId);
        tmsAccessorialAssignment.HubMasterCode           = hubAccessorial.HubMasterCode;
        tmsAccessorialAssignment.HubPosition             = hubAccessorial.HubPosition;
        tmsAccessorialAssignment.AccessorialMasterCode   = hubAccessorial.AccessorialMasterCode;
        tmsAccessorialAssignment.BillingGroupId          = hubAccessorial.BillingGroupId;
        tmsAccessorialAssignment.ExternalCode            = hubAccessorial.ExternalCode;
    }
}

public void executeQuery()
{
    if (carrierId)
    {
        qbr_Carrier.value(carrierId);
    }

    super();
}

public void init()
{
    super();

    if (carrierId)
    {
        qbr_Carrier = this.query().dataSourceTable(tableNum(TMSAccessorialAssignment)).addRange(fieldNum(TMSAccessorialAssignment, CarrierCode));
        qbr_Carrier.status(RangeStatus::Hidden);
    }
}

public int active()
        {
            int         ret;

            ret = super();

            element.selectFeeType(tmsAccessorialAssignment.FeeType);

            tmsAccessorialAssignment_Zone.enabled(tmsAccessorialAssignment.ZoneMasterCode != '');

            switch (true)
            {
                case tmsAccessorialAssignment.CarrierAccessorialId != "":
                    element.enableFields(TMSAccessorialAssignmentTarget::Carrier);
                    break;

                case tmsAccessorialAssignment.HubAccessorialId != "":
                    element.enableFields(TMSAccessorialAssignmentTarget::Hub);
                    break;

                default:
                    element.enableFields(TMSAccessorialAssignmentTarget::None);
            }

            return ret;
        }

public void enableFieldsBasedOnAssignmentTargets()
{
    if (tmsAccessorialAssignment.HubMasterCode != "")
    {
        tmsAccessorialAssignment.State = "";
        tmsAccessorialAssignment.CountryRegion = "";
        tmsAccessorialAssignment.PostalCodeHigh = "";
        tmsAccessorialAssignment.PostalCodeLow = "";
        tmsAccessorialAssignment.ZoneMasterCode = "";
        tmsAccessorialAssignment.Zone = "";
        element.enableFields(TMSAccessorialAssignmentTarget::Hub);
    }
    else if (tmsAccessorialAssignment.CarrierCode != "")
    {
        element.enableFields(TMSAccessorialAssignmentTarget::Carrier);
    }
    else
    {
        element.enableFields(TMSAccessorialAssignmentTarget::None);
    }
}

public void modified()
            {
                super();

                switch (true)
                {
                    case tmsAccessorialAssignment.CarrierAccessorialId != "":
                        element.enableFields(TMSAccessorialAssignmentTarget::Carrier);
                        break;

                    case tmsAccessorialAssignment.HubAccessorialId != "":
                        element.enableFields(TMSAccessorialAssignmentTarget::Hub);
                        break;

                    default:
                        element.enableFields(TMSAccessorialAssignmentTarget::None);
                }
            }

public void lookup(FormControl _formControl, str _filterStr)
{
    TMSAccessorialAssignment::lookupCarrierAccessorial(_formControl, tmsAccessorialAssignment.CarrierCode);
}

public void modified()
{
    super();

    TMSAccessorialAssignment_ds.enableFieldsBasedOnAssignmentTargets();
}

public void modified()
            {
                super();
                TMSAccessorialAssignment_ds.enableFieldsBasedOnAssignmentTargets();
            }

