{
    super();

    TMSAccessorialAssignment_ds.enableFieldsBasedOnAssignmentTargets();
}

public void modified()
            {
                super();

                TMSAccessorialAssignment_ds.enableFieldsBasedOnAssignmentTargets();
            }

public void lookup()
        {
            SysTableLookup          sysTableLookup;
            Query                   query;
            QueryBuildDataSource    queryBuildDataSource;

            sysTableLookup          = SysTableLookup::newParameters(tableNum(TMSZoneMaster), this);
            query                   = new Query();
            queryBuildDataSource    = query.addDataSource(tableNum(TMSZoneMaster));

            sysTableLookup.addLookupfield(fieldNum(TMSZoneMaster, ZoneMasterCode));
            queryBuildDataSource.addRange(fieldNum(TMSZoneMaster, ZoneType)).value(enum2str(TMSZoneType::Routing));

            sysTableLookup.parmQuery(query);
            sysTableLookup.performFormLookup();
        }

public boolean modified()
{
    boolean ret;

    ret = super();

    if (tmsAccessorialAssignment_ZoneMasterCode.valueStr() == '')
    {
        tmsAccessorialAssignment.Zone = '';
        tmsAccessorialAssignment_Zone.enabled(false);
    }
    else
    {
        tmsAccessorialAssignment_Zone.enabled(true);
    }

    return ret;
}

public void lookup()
{
    TMSZoneMasterDetail::lookupZone(this, tmsAccessorialAssignment.ZoneMasterCode);
}

public int selectionChange()
{
    int ret;

    ret = super();

    element.selectFeeType(tmsAccessorialAssignment.FeeType);

    return ret;
}

