TMSCalendarTypeId dialogCalendarTypeId()
{
    Dialog              dialog;
    DialogField         diaType;
    TMSCalendarTypeId   ret;

    dialog  = new Dialog("@TRX36");
    diaType = dialog.addField(extendedTypeStr(TMSCalendarTypeId));
    dialog.run();

    if (dialog.closedOk())
    {
        ret = diaType.value();
    }

    return ret;
}

public void doReread()
{
    tmsAppointment_DS.reread();
    tmsAppointment_DS.refresh();
    tmsAppointment_DS.active();
}

public void enableFields()
{
    refRecord.enabled(tmsAppointment.ApptRefNum != '');

    firm.enabled(tmsAppointment.ApptStatus == TMSApptStatus::Tentative
              || tmsAppointment.ApptStatus == TMSApptStatus::Blind);

    cancel.enabled(tmsAppointment.isActive());

    checkIn.enabled(driverLog && driverLog.isDriverCheckinEnabled());

    checkOut.enabled(driverLog && driverLog.isDriverCheckoutEnabled());

    TMSDriverLog.enabled(tmsAppointment.ApptStatus != TMSApptStatus::Tentative);

    tmsAppointment_ds.allowEdit(tmsAppointment.isActive());
}

public void init()
{
    TMSAppointment      appointment;

    switch (element.args().dataset())
    {
        case tableNum(TMSAppointment):
            appointment = element.args().record();
            apptId      = appointment.ApptId;
            break;

        case tableNum(WHSLoadTable):
            this.initFromLoad();
            break;

        case tableNum(TMSTransportationAppointmentStaging):
            TMSTransportationAppointmentStaging transportationAppointmentStaging = element.args().record();
            apptId = transportationAppointmentStaging.AppointmentId;
    }

    super();

    xSysLastValue::getLast(this);

    if (calendarTypeId)
    {
        tmsCalendarTypeId.text(calendarTypeId);
    }
}

/// <summary>
///     Initialize the form from a load.
/// </summary>
void initFromLoad()
{
    WHSLoadTable        loadTable;

    loadTable   = element.args().record();

    if (loadTable.LoadId)
    {
        refType     = TMSApptRefType::Load;
        refNum      = loadTable.LoadId;
    }
}

