NumberSeqFormHandler numberSeqFormHandler_Appt()
{
    if (!numberSeqFormHandler_Appt)
    {
        numberSeqFormHandler_Appt = NumberSeqFormHandler::newForm(TMSParameters::numRefTMSApptId().NumberSequenceId,
                                                                 element,
                                                                 tmsAppointment_DS,
                                                                 fieldNum(TMSAppointment, ApptId));
    }

    return numberSeqFormHandler_Appt;
}

