{
    tmsAppointment.CalendarTypeId = tmsCalendarTypeId.text();

    super();
}

public int active()
{
    int     ret;

    ret = super();

    select firstonly driverLog
        where driverLog.ApptId == tmsAppointment.ApptId;

    element.enableFields();

    return ret;
}

void create(boolean append = false, boolean extern = false)  // If created externally
{
    tmsAppointment_ApptId.setFocus();

    element.numberSeqFormHandler_Appt().formMethodDataSourceCreatePre();

    super(append);

    if (!extern)
    {
        element.numberSeqFormHandler_Appt().formMethodDataSourceCreate();
    }

    if (tmsCalendarTypeId)
    {
        tmsAppointment.CalendarTypeId = tmsCalendarTypeId.text();
        tmsAppointment.modifiedField(fieldNum(TMSAppointment, CalendarTypeId));
    }

    if (refType == TMSApptRefType::Load  && refNum)
    {
        TMSAppointment.updateFromLoadTable(WHSLoadTable::find(refNum), true);
    }

    tmsAppointment.Name = tmsAppointment.appointmentName();
}

void delete()
{
    ttsbegin;

    element.numberSeqFormHandler_Appt().formMethodDataSourceDelete();

    super();

    ttscommit;
}

public void executeQuery()
{
    qbrCalendarType.value(tmsCalendarTypeId.text());

    if (refNum)
    {
        qbrRefNum.value(refNum);
        qbrRefType.value(queryValue(refType));
    }

    qbrApptStatus.value(this.appointmentStatusRangeValue());

    qbrAppointmentId.value(apptId);

    super();
}

/// <summary>
/// Returns the string value used for the appointment status range.
/// </summary>
/// <returns>The string value used for the appointment status range.</returns>
protected str appointmentStatusRangeValue()
{
    if (element.args().dataset() == tableNum(TMSTransportationAppointmentStaging) || closedFilter.value())
    {
        return '';
    }
    else
    {
        return strFmt('!%1,!%2', TMSApptStatus::Completed, TMSApptStatus::Cancelled);
    }
}

private QueryBuildRange addRangeAndSetStatus(FieldId _fieldId, RangeStatus _rangeStatus)
{
    QueryBuildRange range = this.query().dataSourceTable(tableNum(TMSAppointment)).addRange(_fieldId);
    range.status(_rangeStatus);

    return range;
}

public void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = tmsAppointment_q.dataSourceTable(tableNum(TMSAppointment));
    qbds.clearDynalinks();
    qbds.clearRanges();

    qbrApptStatus = this.addRangeAndSetStatus(fieldNum(TMSAppointment, ApptStatus), RangeStatus::Hidden);

    qbrCalendarType = this.addRangeAndSetStatus(fieldNum(TMSAppointment, CalendarTypeId), RangeStatus::Hidden);

    qbrSiteId = this.addRangeAndSetStatus(fieldNum(TMSAppointment, InventSiteId), RangeStatus::Open);

    qbrLocationId = this.addRangeAndSetStatus(fieldNum(TMSAppointment, InventLocationId), RangeStatus::Open);

    qbrRefType = this.addRangeAndSetStatus(fieldNum(TMSAppointment, ApptRefType), RangeStatus::Hidden);

    qbrRefNum = this.addRangeAndSetStatus(fieldNum(TMSAppointment, ApptRefNum), RangeStatus::Hidden);

    qbrAppointmentId = this.addRangeAndSetStatus(fieldNum(TMSAppointment, ApptId), RangeStatus::Hidden);
}

