public void write()
{
    Dialog        dialog;
    DialogField   reason;
    DialogField   note;
    TMSNote       notes;
    TMSReasonCode reasonCode;
    boolean       savingCancelled;
    TMSApptStatus apptStatusOrig;

    apptStatusOrig = tmsAppointment.orig().ApptStatus;

    if (apptStatusOrig != tmsAppointment.ApptStatus)
    {
        if (apptStatusOrig == TMSApptStatus::Firmed)
        {
            dialog  = new Dialog("@TRX77");
            reason  = dialog.addField(extendedTypeStr(TMSReasonCode));
            note    = dialog.addField(extendedTypeStr(TMSNote));
            dialog.run();
            savingCancelled = dialog.closedOk() == NoYes::Yes ? NoYes::No : NoYes::Yes;

            if (!savingCancelled)
            {
                reasonCode  = reason.value();
                notes       = note.value();
            }
        }
        else
        {
            reasonCode = enum2str(tmsAppointment.ApptStatus);
        }
    }

    if (!savingCancelled)
    {
        super();

        element.numberSeqFormHandler_Appt().formMethodDataSourceWrite();

        tmsAppointment.createHistory(reasonCode, notes);
    }
}

public void dataChanged()
{
    super();

    InventSiteId origInventSiteId = tmsAppointment.orig().InventSiteId;

    if (tmsAppointment.InventSiteId != origInventSiteId)
    {
        if (InventSite::timeZoneSite(tmsAppointment.InventSiteId) != InventSite::timeZoneSite(origInventSiteId))
        {
            info("@WAX:SiteOnDifferentTimeZoneSpecified");
        }
    }
}

