public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        tmsAppointment_ds.executeQuery();

        calendarTypeId = tmsCalendarTypeId.text();
        xSysLastValue::saveLast(element);
    }

    return ret;
}

public boolean modified()
{
    boolean      ret;
    MenuFunction mf;

    if (this.selection() == TMSApptStatus::CheckedIn)
    {
        mf = new MenuFunction(menuitemDisplayStr(TMSDriverCheckIn), MenuItemType::Display);
        warning(strFmt("@TRX:StatusChangeWarning", enum2str(TMSApptStatus::CheckedIn), mf.label()));
    }
    else
    {
        ret = super();

        if (ret)
        {
            TMSAppointment_ds.research(true);
        }
    }

    return ret;
}

