void fillInvoiceHeader(boolean _multipleInvoices)
{
    switch (_multipleInvoices)
    {
        case true:
            internalInvNum.dataSource('');
            internalInvNum.dataMethod('');
            internalInvNum.text("@TRX1164");

            vendInvoiceId.dataSource('');
            vendInvoiceId.dataMethod('');
            vendInvoiceId.text('');

            invoiceStatus.dataSource('');
            invoiceStatus.dataMethod('');
            invoiceStatus.selection(0);

            vendAccount.dataSource('');
            vendAccount.dataMethod('');
            vendACcount.text('');

            vendInvoiceAccount.dataSource('');
            vendInvoiceAccount.dataMethod('');
            vendInvoiceAccount.text('');

            dueDate.dataSource('');
            dueDate.dataMethod('');
            dueDate.dateValue(dateNull());

            cashDiscDate.dataSource('');
            cashDiscDate.dataMethod('');
            cashDiscDate.dateValue(dateNull());
            break;

        case false:
            internalInvNum.dataSource(identifierStr(TMSInvoiceLine));
            internalInvNum.dataMethod(tableMethodStr(TMSInvoiceLine, displayInternalInvNum));

            vendInvoiceId.dataSource(identifierStr(TMSInvoiceLine));
            vendInvoiceId.dataMethod(tableMethodStr(TMSInvoiceLine, displayVendInvoiceId));

            invoiceStatus.dataSource(identifierStr(TMSInvoiceLine));
            invoiceStatus.dataMethod(tableMethodStr(TMSInvoiceLine, displayInvoiceStatus));

            vendAccount.dataSource(identifierStr(TMSInvoiceLine));
            vendAccount.dataMethod(tableMethodStr(TMSInvoiceLine, displayVendAccount));

            vendInvoiceAccount.dataSource(identifierStr(TMSInvoiceLine));
            vendInvoiceAccount.dataMethod(tableMethodStr(TMSInvoiceLine, displayVendInvoiceAccount));

            dueDate.dataSource(identifierStr(TMSInvoiceLine));
            dueDate.dataMethod(tableMethodStr(TMSInvoiceLine, displayDueDate));

            cashDiscDate.dataSource(identifierStr(TMSInvoiceLine));
            cashDiscDate.dataMethod(tableMethodStr(TMSInvoiceLine, displaycashDiscDate));
            break;
    }
}

public str freightBillFilter(QueryBuildDataSource _qbds)
{
    str         ret;

    if (reconcileType != TMSFreightBillReconcileType::Invoice)
    {
        if (freightBillFilter.VendorCode)
        {
            ret = strFmt('((%1.%2 == "%3"))', _qbds.name(), fieldStr(TMSFreightBillDetail, VendorCode), freightBillFilter.VendorCode);
        }

        if (freightBillFilter.VendorInvoiceCode)
        {
            ret += ret ? '&&' : '';
            ret += strFmt('((%1.%2 == "%3"))', _qbds.name(), fieldStr(TMSFreightBillDetail, VendorInvoiceCode), freightBillFilter.VendorInvoiceCode);
        }

        if (freightBillFilter.ShipUTCDateTimeFrom)
        {
            ret += ret ? '&&' : '';
            ret += strFmt('((%1.%2 >= %3))', _qbds.name(), fieldStr(TMSFreightBillDetail, ShipUTCDateTime), queryValue(freightBillFilter.ShipUTCDateTimeFrom));
        }

        if (freightBillFilter.ShipUTCDateTimeTo)
        {
            ret += ret ? '&&' : '';
            ret += strFmt('((%1.%2 <= %3))', _qbds.name(), fieldStr(TMSFreightBillDetail, ShipUTCDateTime), queryValue(freightBillFilter.ShipUTCDateTimeTo));
        }

        if (freightBillFilter.CarrierCode)
        {
            ret += ret ? '&&' : '';
            ret += strFmt('((%1.%2 == "%3"))', _qbds.name(), fieldStr(TMSFreightBillDetail, CarrierCode), freightBillFilter.CarrierCode);
        }

        if (freightBillFilter.ShipmentId)
        {
            ret += ret ? '&&' : '';
            ret += strFmt('((%1.%2 == "%3"))', _qbds.name(), fieldStr(TMSFreightBillDetail, ShipmentId), freightBillFilter.ShipmentId);
        }

        if (freightBillFilter.LoadId)
        {
            ret += ret ? '&&' : '';
            ret += strFmt('((%1.%2 == "%3"))', _qbds.name(), fieldStr(TMSFreightBillDetail, LoadId), freightBillFilter.LoadId);
        }

        if (freightBillFilter.RouteCode)
        {
            ret += ret ? '&&' : '';
            ret += strFmt('((%1.%2 == "%3"))', _qbds.name(), fieldStr(TMSFreightBillDetail, RouteCode), freightBillFilter.RouteCode);
        }

        if (freightBillFilter.AddressCountryRegionId)
        {
            ret += ret ? '&&' : '';
            ret += strFmt('((%1.%2 == "%3"))', _qbds.name(), fieldStr(TMSFreightBillDetailUnMatched, DestinationCountryRegion), freightBillFilter.AddressCountryRegionId);
        }

        // Following fields will evaluate partial strings
        if (freightBillFilter.BillOfLadingId)
        {
            ret += ret ? '&&' : '';
            ret += strFmt('((%1.%2 like "%3"))', _qbds.name(), fieldStr(TMSFreightBillDetail, BillOfLadingId), freightBillFilter.BillOfLadingId);
        }

        if (freightBillFilter.VoyageNum)
        {
            ret += ret ? '&&' : '';
            ret += strFmt('((%1.%2 like "%3"))', _qbds.name(), fieldStr(TMSFreightBillDetail, VoyageNum), freightBillFilter.VoyageNum);
        }

        if (freightBillFilter.VesselName)
        {
            ret += ret ? '&&' : '';
            ret += strFmt('((%1.%2 like "*%3*"))', _qbds.name(), fieldStr(TMSFreightBillDetail, VesselName), freightBillFilter.VesselName);
        }

        if (freightBillFilter.RelatedOrderNum)
        {
            ret += ret ? '&&' : '';
            ret += strFmt('((%1.%2 like "*%3*"))', _qbds.name(), fieldStr(TMSFreightBillDetail, RelatedOrderNum), freightBillFilter.RelatedOrderNum);
        }

        if (freightBillFilter.RelatedAccountNum)
        {
            ret += ret ? '&&' : '';
            ret += strFmt('((%1.%2 like "*%3*"))', _qbds.name(), fieldStr(TMSFreightBillDetail, RelatedAccountNum), freightBillFilter.RelatedAccountNum);
        }

        if (freightBillFilter.TrailerNumber)
        {
            ret += ret ? '&&' : '';
            ret += strFmt('((%1.%2 like "*%3*"))', _qbds.name(), fieldStr(TMSFreightBillDetail, TrailerNumber), freightBillFilter.TrailerNumber);
        }
    }

    return ret;
}

public void init()
{
    TMSInvoiceTable         invoiceTable;
    TMSFreightBillDetail    freightBillDetail;
    int                     showSubAppValue = 0;

    if (!element.args().record())
    {
        throw error("@SYS22539");
    }

    switch (element.args().dataset())
    {
        case tableNum(TMSInvoiceTable):
            invoiceTable = element.args().record() as TMSInvoiceTable;
            freightBillFilter = TMSTmpFreightBillFilter::invoiceFilter(invoiceTable.InternalInvNumber);
            reconcileType = TMSFreightBillReconcileType::Invoice;
            showSubAppValue = 1;
            break;

        case tableNum(TMSFreightBillDetail):
            freightBillDetail = element.args().record() as TMSFreightBillDetail;
            freightBillFilter = TMSTmpFreightBillFilter::routeCodeFilter(freightBillDetail.RouteCode);
            reconcileType = TMSFreightBillReconcileType::FreightBill;
            break;

        case tableNum(TMSTmpFreightBillFilter):
            freightBillFilter = element.args().record() as TMSTmpFreightBillFilter;
            break;
    }

    super();

    showSubApp.value(showSubAppValue);
}

